   887â†’
   888â†’/* Print styles */
   889â†’@media print {
   890â†’  .no-print {
   891â†’    display: none !important;
   892â†’  }
   893â†’  
   894â†’  .notifications-container,
   895â†’  .chat-widget-container,
   896â†’  .connection-indicator {
   897â†’    display: none !important;
   898â†’  }
   899â†’  
   900â†’  * {
   901â†’    background: transparent !important;
   902â†’    color: black !important;
   903â†’    box-shadow: none !important;
   904â†’    text-shadow: none !important;
   905â†’  }
   906â†’}
   907â†’
   908â†’/* ===== MOBILE OPTIMIZATIONS ===== */
   909â†’
   910â†’/* Touch optimization styles */
   911â†’.touch-optimized {
   912â†’  -webkit-tap-highlight-color: transparent;
   913â†’  -webkit-touch-callout: none;
   914â†’  -webkit-user-select: none;
   915â†’  user-select: none;
   916â†’}
   917â†’
   918â†’.touch-optimized button,
   919â†’.touch-optimized [role="button"],
   920â†’.touch-optimized a,
   921â†’.touch-optimized input,
   922â†’.touch-optimized select,
   923â†’.touch-optimized textarea {
   924â†’  -webkit-user-select: text;
   925â†’  user-select: text;
   926â†’}
   927â†’
   928â†’/* Large touch targets for mobile */
   929â†’.large-touch-targets button,
   930â†’.large-touch-targets [role="button"],
   931â†’.large-touch-targets a,
   932â†’.large-touch-targets .btn-base {
   933â†’  min-height: 44px;
   934â†’  min-width: 44px;
   935â†’  padding: var(--spacing-3) var(--spacing-4);
   936â†’}
   937â†’
   938â†’.large-touch-targets .btn-sm {
   939â†’  min-height: 40px;
   940â†’  min-width: 40px;
   941â†’}
   942â†’
   943â†’.large-touch-targets .btn-lg {
   944â†’  min-height: 48px;
   945â†’  min-width: 48px;
   946â†’}
   947â†’
   948â†’/* Performance mode optimizations */
   949â†’.performance-mode * {
   950â†’  will-change: auto !important;
   951â†’  animation-duration: 0.1s !important;
   952â†’  transition-duration: 0.1s !important;
   953â†’}
   954â†’
   955â†’.performance-mode .glass-medium,
   956â†’.performance-mode .glass-strong {
   957â†’  backdrop-filter: none !important;
   958â†’  -webkit-backdrop-filter: none !important;
   959â†’  background: var(--color-surface-primary) !important;
   960â†’}
   961â†’
   962â†’/* Reduced animations for accessibility and battery */
   963â†’.reduce-animations * {
   964â†’  animation-duration: 0.01ms !important;
   965â†’  animation-iteration-count: 1 !important;
   966â†’  transition-duration: 0.01ms !important;
   967â†’}
   968â†’
   969â†’.reduce-animations .animate-pulse,
   970â†’.reduce-animations .animate-bounce,
   971â†’.reduce-animations .animate-spin {
   972â†’  animation: none !important;
   973â†’}
   974â†’
   975â†’/* Mobile-specific breakpoints */
   976â†’@media (max-width: 480px) {
   977â†’  /* Extra small mobile phones */
   978â†’  .responsive-mobile-xs {
   979â†’    font-size: 14px;
   980â†’    padding: var(--spacing-2);
   981â†’  }
   982â†’  
   983â†’  .btn-base {
   984â†’    font-size: var(--font-size-sm);
   985â†’    padding: var(--spacing-2) var(--spacing-3);
   986â†’  }
   987â†’  
   988â†’  .modal-backdrop {
   989â†’    backdrop-filter: none;
   990â†’    -webkit-backdrop-filter: none;
   991â†’  }
   992â†’}
   993â†’
   994â†’@media (max-width: 640px) {
   995â†’  /* Small mobile devices */
   996â†’  .responsive-mobile-sm {
   997â†’    padding: var(--spacing-3);
   998â†’  }
   999â†’  
  1000â†’  .card-base {
  1001â†’    border-radius: var(--radius-lg);
  1002â†’    margin: var(--spacing-2);
  1003â†’  }
  1004â†’  
  1005â†’  .glass-medium,
  1006â†’  .glass-strong {
  1007â†’    backdrop-filter: blur(8px) !important;
  1008â†’    -webkit-backdrop-filter: blur(8px) !important;
  1009â†’  }
  1010â†’}
  1011â†’
  1012â†’/* Landscape mobile orientation */
  1013â†’@media (max-height: 500px) and (orientation: landscape) {
  1014â†’  .landscape-optimize {
  1015â†’    height: 100vh;
  1016â†’    overflow-y: auto;
  1017â†’  }
  1018â†’  
  1019â†’  .notifications-container {
  1020â†’    top: var(--spacing-2);
  1021â†’    right: var(--spacing-2);
  1022â†’  }
  1023â†’  
  1024â†’  .chat-widget-container {
  1025â†’    bottom: var(--spacing-2);
  1026â†’    right: var(--spacing-2);
  1027â†’  }
  1028â†’}
  1029â†’
  1030â†’/* High DPI displays (retina) */
  1031â†’@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 2dppx) {
  1032â†’  .retina-optimize {
  1033â†’    image-rendering: -webkit-optimize-contrast;
  1034â†’    image-rendering: crisp-edges;
  1035â†’  }
  1036â†’  
  1037â†’  .retina-optimize img {
  1038â†’    image-rendering: -webkit-optimize-contrast;
  1039â†’    image-rendering: crisp-edges;
  1040â†’  }
  1041â†’}
  1042â†’
  1043â†’/* Hover detection for touch devices */
  1044â†’@media (hover: none) {
  1045â†’  .hover-only {
  1046â†’    display: none !important;
  1047â†’  }
  1048â†’  
  1049â†’  .btn-base:hover {
  1050â†’    transform: none !important;
  1051â†’  }
  1052â†’  
  1053â†’  .card-base:hover {
  1054â†’    transform: none !important;
  1055â†’    box-shadow: var(--shadow-sm) !important;
  1056â†’  }
  1057â†’}
  1058â†’
  1059â†’/* Pointer device optimization */
  1060â†’@media (pointer: coarse) {
  1061â†’  /* Touch devices */
  1062â†’  .form-control-base {
  1063â†’    font-size: 16px; /* Prevents zoom on iOS */
  1064â†’    padding: var(--spacing-3);
  1065â†’  }
  1066â†’  
  1067â†’  .btn-base {
  1068â†’    padding: var(--spacing-3) var(--spacing-4);
  1069â†’  }
  1070â†’  
  1071â†’  select,
  1072â†’  input,
  1073â†’  textarea {
  1074â†’    font-size: 16px; /* Prevents zoom on iOS */
  1075â†’  }
  1076â†’}
  1077â†’
  1078â†’/* Safe area insets for devices with notches */
  1079â†’@supports (padding: max(0px)) {
  1080â†’  .safe-area-inset-top {
  1081â†’    padding-top: max(var(--spacing-4), env(safe-area-inset-top));
  1082â†’  }
  1083â†’  
  1084â†’  .safe-area-inset-bottom {
  1085â†’    padding-bottom: max(var(--spacing-4), env(safe-area-inset-bottom));
  1086â†’  }
  1087â†’  
  1088â†’  .safe-area-inset-left {
  1089â†’    padding-left: max(var(--spacing-4), env(safe-area-inset-left));
  1090â†’  }
  1091â†’  
  1092â†’  .safe-area-inset-right {
  1093â†’    padding-right: max(var(--spacing-4), env(safe-area-inset-right));
  1094â†’  }
  1095â†’}
  1096â†’
  1097â†’/* PWA display modes */
  1098â†’@media (display-mode: standalone) {
  1099â†’  .pwa-standalone {
  1100â†’    padding-top: var(--spacing-4);
  1101â†’  }
  1102â†’  
  1103â†’  .pwa-standalone .navbar {
  1104â†’    padding-top: env(safe-area-inset-top);
  1105â†’  }
  1106â†’}
  1107â†’
  1108â†’/* Connection-aware styles */
  1109â†’.slow-connection .glass-medium,
  1110â†’.slow-connection .glass-strong {
  1111â†’  backdrop-filter: none !important;
  1112â†’  -webkit-backdrop-filter: none !important;
  1113â†’  background: var(--color-surface-primary) !important;
  1114â†’}
  1115â†’
  1116â†’.slow-connection img {
  1117â†’  filter: blur(2px);
  1118â†’  transition: filter var(--transition-base);
  1119â†’}
  1120â†’
  1121â†’.slow-connection img.loaded {
  1122â†’  filter: none;
  1123â†’}
  1124â†’
  1125â†’/* Gesture feedback */
  1126â†’.gesture-feedback {
  1127â†’  position: relative;
  1128â†’  overflow: hidden;
  1129â†’}
  1130â†’
  1131â†’.gesture-feedback::after {
  1132â†’  content: '';
  1133â†’  position: absolute;
  1134â†’  top: 50%;
  1135â†’  left: 50%;
  1136â†’  width: 0;
  1137â†’  height: 0;
  1138â†’  border-radius: 50%;
  1139â†’  background: rgba(0, 0, 0, 0.1);
  1140â†’  transform: translate(-50%, -50%);
  1141â†’  transition: width 0.3s ease, height 0.3s ease;
  1142â†’  pointer-events: none;
  1143â†’}
  1144â†’
  1145â†’.gesture-feedback.active::after {
  1146â†’  width: 100px;
  1147â†’  height: 100px;
  1148â†’}
  1149â†’
  1150â†’/* Dark mode gesture feedback */
  1151â†’[data-theme="dark"] .gesture-feedback::after {
  1152â†’  background: rgba(255, 255, 255, 0.1);
  1153â†’}
  1154â†’
  1155â†’/* Mobile navigation improvements - removed unused mobile-nav-optimize styles that caused gray bar at bottom */
  1156â†’
  1157â†’/* Swipe gesture indicators */
  1158â†’.swipe-indicator {
  1159â†’  position: relative;
  1160â†’}
  1161â†’
  1162â†’.swipe-indicator::before {
  1163â†’  content: '';
  1164â†’  position: absolute;
  1165â†’  top: 50%;
  1166â†’  right: var(--spacing-4);
  1167â†’  width: 20px;
  1168â†’  height: 2px;
  1169â†’  background: var(--color-gray-400);
  1170â†’  transform: translateY(-50%);
  1171â†’  opacity: 0.5;
  1172â†’}
  1173â†’
  1174â†’.swipe-indicator::after {
  1175â†’  content: '';
  1176â†’  position: absolute;
  1177â†’  top: 50%;
  1178â†’  right: var(--spacing-3);
  1179â†’  width: 0;
  1180â†’  height: 0;
  1181â†’  border-left: 6px solid var(--color-gray-400);
  1182â†’  border-top: 4px solid transparent;
  1183â†’  border-bottom: 4px solid transparent;
  1184â†’  transform: translateY(-50%);
  1185â†’  opacity: 0.5;
  1186â†’}
  1187â†’
  1188â†’/* Loading states optimized for mobile */
  1189â†’.mobile-loading {
  1190â†’  display: flex;
  1191â†’  flex-direction: column;
  1192â†’  align-items: center;
  1193â†’  justify-content: center;
  1194â†’  min-height: 200px;
  1195â†’  padding: var(--spacing-8);
  1196â†’}
  1197â†’
  1198â†’.mobile-loading .loading-spinner {
  1199â†’  width: 40px;
  1200â†’  height: 40px;
  1201â†’  border: 3px solid var(--color-gray-200);
  1202â†’  border-top: 3px solid var(--color-primary-500);
  1203â†’  border-radius: 50%;
  1204â†’  animation: spin 1s linear infinite;
  1205â†’}
  1206â†’
  1207â†’.mobile-loading .loading-text {
  1208â†’  margin-top: var(--spacing-4);
  1209â†’  font-size: var(--font-size-sm);
  1210â†’  color: var(--color-text-secondary);
  1211â†’  text-align: center;
  1212â†’}
  1213â†’
  1214â†’/* Battery optimization styles */
  1215â†’.battery-optimize {
  1216â†’  animation-play-state: paused !important;
  1217â†’}
  1218â†’
  1219â†’.battery-optimize * {
  1220â†’  animation-play-state: paused !important;
  1221â†’  transition-duration: 0.1s !important;
  1222â†’}
  1223â†’
  1224â†’.battery-optimize .glass-medium,
  1225â†’.battery-optimize .glass-strong {
  1226â†’  backdrop-filter: none !important;
  1227â†’  -webkit-backdrop-filter: none !important;
  1228â†’  background: var(--color-surface-primary) !important;
  1229â†’}
  1230â†’
  1231â†’.battery-optimize .animate-pulse,
  1232â†’.battery-optimize .animate-bounce,
  1233â†’.battery-optimize .animate-spin {
  1234â†’  animation: none !important;
  1235â†’}
  1236â†’
  1237â†’/* Accessibility enhancements for mobile */
  1238â†’@media (max-width: 768px) {
  1239â†’  .mobile-a11y button,
  1240â†’  .mobile-a11y [role="button"],
  1241â†’  .mobile-a11y input,
  1242â†’  .mobile-a11y select {
  1243â†’    min-height: 44px;
  1244â†’    font-size: 16px;
  1245â†’  }
  1246â†’  
  1247â†’  .mobile-a11y .focus-ring:focus-visible {
  1248â†’    outline: 3px solid var(--color-primary-500);
