//    - Update: Users
//    - Delete: Users

// 3. Set document permissions on create:
await databases.createDocument(
  dbId,
  collId,
  ID.unique(),
  data,
  [
    Permission.read(Role.user(userId)),
    Permission.update(Role.user(userId)),
    Permission.delete(Role.user(userId))
  ]
)
