     1→<template>
     2→  <component
     3→    :is="as"
     4→    :class="[
     5→      'inline-flex items-center justify-center rounded-lg font-medium transition-all duration-fast border',
     6→      sizeClass,
     7→      variantClass,
     8→      {
     9→        'opacity-60 pointer-events-none cursor-not-allowed transform-none': disabled || loading,
    10→        'cursor-pointer transform hover:scale-[1.02] active:scale-[0.98]': !disabled && !loading,
    11→      },
    12→      props.class,
    13→    ]"
    14→    :disabled="disabled || loading"
    15→    :href="as === 'a' ? href : undefined"
    16→    :to="as === 'router-link' ? to : undefined"
    17→    :type="as === 'button' ? type : undefined"
    18→    @click="handleClick"
    19→  >
    20→    <!-- Left icon or loading spinner -->
    21→    <Icon 
    22→      v-if="icon && (!iconPosition || iconPosition === 'left') && !loading" 
    23→      :icon="icon" 
    24→      :class="iconClass" 
    25→    />
    26→    <div 
    27→      v-if="loading && (!iconPosition || iconPosition === 'left')" 
    28→      :class="spinnerClass"
    29→    ></div>
    30→
    31→    <!-- Button text -->
    32→    <span 
    33→      v-if="text || $slots.default" 
    34→      :class="[
    35→        textClass,
    36→        { 'ml-2': (icon || loading) && (!iconPosition || iconPosition === 'left') },
    37→        { 'mr-2': (icon || loading) && iconPosition === 'right' }
    38→      ]"
    39→    >
    40→      <slot>{{ text }}</slot>
    41→    </span>
    42→
    43→    <!-- Right icon or loading spinner -->
    44→    <Icon 
    45→      v-if="icon && iconPosition === 'right' && !loading" 
    46→      :icon="icon" 
    47→      :class="iconClass" 
    48→    />
    49→    <div 
    50→      v-if="loading && iconPosition === 'right'" 
    51→      :class="spinnerClass"
    52→    ></div>
    53→  </component>
    54→</template>
    55→
    56→<script setup lang="ts">
    57→import { computed } from "vue";
    58→import { Icon } from "@iconify/vue";
    59→
    60→interface Props {
    61→  variant?: "primary" | "secondary" | "outline" | "ghost" | "danger" | "success" | "warning";
    62→  size?: "xs" | "sm" | "md" | "lg" | "xl";
    63→  text?: string;
    64→  icon?: string;
    65→  iconPosition?: "left" | "right";
    66→  loading?: boolean;
    67→  disabled?: boolean;
    68→  as?: "button" | "a" | "router-link";
    69→  type?: "button" | "submit" | "reset";
    70→  href?: string;
    71→  to?: string | object;
    72→  class?: string;
    73→  iconClass?: string;
    74→  textClass?: string;
    75→}
    76→
    77→const props = withDefaults(defineProps<Props>(), {
    78→  variant: "primary",
    79→  size: "md",
    80→  iconPosition: "left",
    81→  as: "button",
    82→  type: "button",
    83→});
    84→
    85→const emit = defineEmits<{
    86→  click: [event: MouseEvent];
    87→}>();
    88→
    89→const sizeClass = computed(() => {
    90→  const sizeMap = {
    91→    xs: "btn-xs px-2 py-1 text-xs gap-1",
    92→    sm: "btn-sm px-3 py-2 text-sm gap-2",
    93→    md: "btn-md px-4 py-2 text-base gap-2",
    94→    lg: "btn-lg px-6 py-3 text-lg gap-3",
    95→    xl: "btn-xl px-8 py-4 text-xl gap-3",
    96→  };
    97→  return sizeMap[props.size];
    98→});
    99→
   100→const variantClass = computed(() => {
   101→  const variantMap = {
   102→    primary: "bg-primary-500 hover:bg-primary-600 active:bg-primary-700 text-white border-transparent shadow-md hover:shadow-lg focus-ring",
   103→    secondary: "bg-gray-500 hover:bg-gray-600 active:bg-gray-700 text-white border-transparent shadow-md hover:shadow-lg focus-ring",
   104→    outline: "border-2 border-primary-500 text-primary-600 hover:bg-primary-500 hover:text-white dark:text-primary-400 dark:hover:text-white bg-transparent shadow-sm hover:shadow-md focus-ring",
   105→    ghost: "text-primary-600 hover:bg-primary-50 active:bg-primary-100 dark:text-primary-400 dark:hover:bg-primary-950 dark:active:bg-primary-900 border-transparent focus-ring",
   106→    danger: "bg-error-500 hover:bg-error-600 active:bg-error-700 text-white border-transparent shadow-md hover:shadow-lg focus-ring",
   107→    success: "bg-success-500 hover:bg-success-600 active:bg-success-700 text-white border-transparent shadow-md hover:shadow-lg focus-ring",
   108→    warning: "bg-warning-500 hover:bg-warning-600 active:bg-warning-700 text-white border-transparent shadow-md hover:shadow-lg focus-ring",
   109→  };
   110→  return variantMap[props.variant];
   111→});
   112→
   113→const iconClass = computed(() => {
   114→  const iconSizeMap = {
   115→    xs: "w-3 h-3",
   116→    sm: "w-4 h-4",
   117→    md: "w-4 h-4",
   118→    lg: "w-5 h-5",
   119→    xl: "w-6 h-6",
   120→  };
   121→  return props.iconClass || iconSizeMap[props.size];
   122→});
   123→
   124→const textClass = computed(() => {
   125→  return props.textClass || "font-medium";
   126→});
   127→
   128→const spinnerClass = computed(() => {
   129→  const baseSpinner = "border-2 border-t-transparent rounded-full animate-spin";
   130→  const sizeMap = {
   131→    xs: "w-3 h-3",
   132→    sm: "w-4 h-4", 
   133→    md: "w-4 h-4",
   134→    lg: "w-5 h-5",
   135→    xl: "w-6 h-6",
   136→  };
   137→  
   138→  const colorMap = {
   139→    primary: "border-white",
   140→    secondary: "border-white",
   141→    outline: "border-primary-500",
   142→    ghost: "border-primary-500",
   143→    danger: "border-white",
   144→    success: "border-white",
   145→    warning: "border-white",
   146→  };
   147→  
   148→  return `${baseSpinner} ${sizeMap[props.size]} ${colorMap[props.variant]}`;
   149→});
   150→
   151→const handleClick = (event: MouseEvent) => {
   152→  if (!props.disabled && !props.loading) {
   153→    emit("click", event);
   154→  }
   155→};
   156→</script>
