     1→<template>
     2→  <div class="relative mt-2 mb-4">
     3→    <label v-if="label"
     4→      class="absolute text-sm font-medium text-gray-900 dark:text-gray-100 left-3 label-position px-1 z-10 transition-colors duration-base">
     5→      {{ label }}
     6→      <span v-if="required" class="text-error-500 font-medium">*</span>
     7→    </label>
     8→    <div class="relative flex items-center">
     9→      <Icon v-if="icon && mounted" :icon="icon" class="absolute left-3 text-gray-400 dark:text-gray-500 w-5 h-5 transition-colors duration-base pointer-events-none" />
    10→      <input :value="modelValue" @input="handleInput" @blur="handleBlur" :class="[
    11→        'form-control-base w-full py-3 px-4 h-12 transition-all duration-base focus-ring',
    12→        props.class ?? '',
    13→        inputClass,
    14→        icon ? 'pl-10' : '',
    15→      ]" :type="type || 'text'" :placeholder="placeholder" :required="required" />
    16→    </div>
    17→    <p v-if="errorMessage" class="mt-2 text-sm text-error-600 dark:text-error-400 animate-fade-in">
    18→      {{ errorMessage }}
    19→    </p>
    20→  </div>
    21→</template>
    22→<script setup lang="ts">
    23→import { computed, ref } from "vue";
    24→import { useMounted } from "@vueuse/core";
    25→import { Icon } from "@iconify/vue";
    26→
    27→const props = defineProps<{
    28→  isValid?: number;
    29→  isValidTest?: (value: string) => boolean;
    30→  invalidMessage?: string;
    31→  type?: string;
    32→  placeholder?: string;
    33→  icon?: string;
    34→  label?: string;
    35→  class?: string;
    36→  required?: boolean;
    37→}>();
    38→
    39→const modelValue = defineModel<string>("modelValue", {
    40→  default: "",
    41→});
    42→
    43→const mounted = useMounted();
    44→const hasBlurredInput = ref(false);
    45→const errorMessage = ref("");
    46→
    47→const inputClass = computed(() => {
    48→  if (!mounted.value) return "";
    49→  if (!hasBlurredInput.value) return "";
    50→  if (props.isValidTest) {
    51→    return props.isValidTest(modelValue.value) || props.isValid
    52→      ? "success !border-success-500 focus:!shadow-focus-success"
    53→      : "error !border-error-500 focus:!shadow-focus-error";
    54→  }
    55→  if (props.required && !modelValue.value) {
    56→    return "error !border-error-500 focus:!shadow-focus-error";
    57→  }
    58→  if (props.isValid === undefined) return "";
    59→  return props.isValid 
    60→    ? "success !border-success-500 focus:!shadow-focus-success" 
    61→    : "error !border-error-500 focus:!shadow-focus-error";
    62→});
    63→
    64→const handleInput = (event: Event) => {
    65→  const target = event.target as HTMLInputElement;
    66→  modelValue.value = target.value;
    67→  // Only clear error messages when typing if field becomes valid
    68→  if (hasBlurredInput.value && errorMessage.value) {
    69→    if (props.isValidTest && props.isValidTest(target.value)) {
    70→      errorMessage.value = "";
    71→    } else if (props.required && target.value.trim()) {
    72→      errorMessage.value = "";
    73→    }
    74→  }
    75→};
    76→
    77→const handleBlur = () => {
    78→  hasBlurredInput.value = true;
    79→  validateInput();
    80→};
    81→
    82→const validateInput = () => {
    83→  errorMessage.value = "";
    84→  if (props.required && !modelValue.value) {
    85→    errorMessage.value = "This field is required";
    86→  } else if (props.isValidTest && !props.isValidTest(modelValue.value)) {
    87→    errorMessage.value = props.invalidMessage || "Invalid input";
    88→  }
    89→};
    90→</script>

