     2→  <Teleport to="#teleport-layer" defer>
     3→    <TransitionRoot appear :show="open" as="template">
     4→      <Dialog as="div" @close="handleClose" class="relative" style="z-index: var(--z-index-modal)">
     5→        <TransitionChild as="template" enter="duration-medium ease-out" enter-from="opacity-0" enter-to="opacity-100"
     6→          leave="duration-fast ease-in" leave-from="opacity-100" leave-to="opacity-0">
