    10→
    11→        <div class="fixed inset-0" :class="props.class" style="z-index: var(--z-index-modal)">
    12→          <div class="flex items-center justify-center min-h-screen px-4 pt-4 pb-20 text-center sm:block sm:p-0">
    13→            <span class="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
    14→
    15→            <TransitionChild as="template" enter="duration-medium ease-out" enter-from="opacity-0 scale-95"
    16→              enter-to="opacity-100 scale-100" leave="duration-fast ease-in" leave-from="opacity-100 scale-100"
    17→              leave-to="opacity-0 scale-95">
    18→              <DialogPanel ref="modalRef" :style="originStyle" :class="modalClasses">
    19→                <DialogTitle as="div" id="modal-title">
    20→                  <slot name="title">Modal Title</slot>
    21→                </DialogTitle>
    22→
    23→                <slot name="content">
    24→                  Modal content goes here
    25→                </slot>
    26→
    27→                <slot name="footer">
    28→                  <div class="px-6 py-4 bg-gray-50/50 dark:bg-gray-800/50 sm:px-6 sm:flex sm:flex-row-reverse border-t border-gray-200 dark:border-gray-700">
    29→                    <PrimaryButton @click="handleSubmit" text="Submit" class="w-full sm:w-auto focus-ring transition-base" />
    30→                    <OutlineButton @click="handleClose" text="Cancel" class="mt-3 w-full sm:mt-0 sm:ml-3 sm:w-auto focus-ring transition-base" />
    31→                  </div>
    32→                </slot>
    33→              </DialogPanel>
    34→            </TransitionChild>
    35→          </div>
    36→        </div>
    37→      </Dialog>
    38→    </TransitionRoot>
    39→  </Teleport>
    40→</template>
    41→<script setup lang="ts">
    42→import { ref, watch, computed } from "vue";
    43→import {
    44→  Dialog,
    45→  DialogPanel,
    46→  DialogTitle,
    47→  TransitionChild,
    48→  TransitionRoot,
    49→} from "@headlessui/vue";
    50→import { useMounted, onClickOutside } from "@vueuse/core";
    51→import PrimaryButton from "@vue/buttons/PrimaryButton.vue";
    52→import OutlineButton from "@vue/buttons/OutlineButton.vue";
    53→
    54→const props = defineProps<{
    55→  closeOnClickOutside?: boolean;
    56→  closeOnEscape?: boolean;
    57→  onSubmit?: Function;
    58→  class?: string;
    59→  contentClass?: string;
    60→  originX?: number;
    61→  originY?: number;
    62→  disableAutoScroll?: boolean;
    63→  maxHeight?: string;
    64→}>();
    65→
    66→const emit = defineEmits<{
    67→  (e: "close"): void;
    68→}>();
    69→
    70→const open = defineModel("open", {
    71→  default: false,
    72→});
    73→
    74→const modalRef = ref<HTMLElement | null>(null);
    75→const mounted = useMounted();
    76→
    77→// Global modal visibility event for tutorial arrow suspension
    78→const MODAL_EVENT = 'ui:modal-visibility';
    79→
    80→watch(open, (isOpen) => {
    81→  // Guard for SSR - only dispatch on client
    82→  if (typeof window !== 'undefined') {
    83→    window.dispatchEvent(
    84→      new CustomEvent(MODAL_EVENT, { detail: { isOpen } })
    85→    );
    86→  }
    87→}, { immediate: true });
    88→
    89→const originStyle = computed(() => {
    90→  if (props.originX && props.originY) {
    91→    return {
    92→      "--origin-x": `${props.originX}px`,
    93→      "--origin-y": `${props.originY}px`,
    94→      "transform-origin": "var(--origin-x) var(--origin-y)",
    95→    };
    96→  }
    97→  return {};
    98→});
    99→
   100→const modalClasses = computed(() => {
   101→  const baseClasses = 'inline-block overflow-hidden text-left align-bottom transition-all transform glass-strong rounded-2xl shadow-xl sm:my-6 sm:align-middle border border-gray-200 dark:border-gray-700';
   102→  
   103→  if (props.disableAutoScroll) {
   104→    // When auto-scroll is disabled, don't add height/overflow constraints
   105→    return [baseClasses, props.contentClass];
   106→  }
   107→  
   108→  // Default behavior: add max-height and overflow-y-auto for backward compatibility
   109→  const heightClass = props.maxHeight || 'max-h-[95vh]';
   110→  return [baseClasses, heightClass, 'overflow-y-auto', props.contentClass];
   111→});
   112→
   113→watch(
   114→  mounted,
   115→  () => {
   116→    if (mounted.value && props.closeOnEscape) {
   117→      window.addEventListener("keydown", handleEscapeKey);
   118→    }
   119→  },
   120→  { immediate: true }
   121→);
   122→
   123→onClickOutside(modalRef, (refValue) => {
   124→  if (!refValue) return;
   125→  if (props.closeOnClickOutside) {
   126→    handleClose();
   127→  }
   128→});
   129→
   130→function handleEscapeKey(event: KeyboardEvent) {
   131→  if (event.key === "Escape") {
   132→    handleClose();
   133→  }
   134→}
   135→
   136→function handleClose() {
   137→  open.value = false;
   138→  emit("close");
   139→}
   140→
   141→function handleSubmit() {
   142→  props.onSubmit?.();
   143→}
   144→
   145→function handleBackdropClick(event: MouseEvent) {
   146→  if (props.closeOnClickOutside && event.target === event.currentTarget) {
   147→    handleClose();
   148→  }
   149→}
   150→</script>
