  () => {
    if (!props.active) {
      beamLines.value = []
      beamRafControls.pause()
      return
    }

    const theater = theaterRef.value
    const orbContainer = orbContainerRef.value
    const cardsContainer = cardsContainerRef.value

    if (!theater || !orbContainer || !cardsContainer) {
      beamLines.value = []
      return
    }

    // ============================================
    // PHASE 1: BATCH ALL READS (no writes!)
    // ============================================
    const theaterRect = theater.getBoundingClientRect()
    const orbRect = orbContainer.getBoundingClientRect()
    const cardElements = Array.from(cardsContainer.querySelectorAll('.card-item')) as HTMLElement[]

    if (!cardElements.length) {
      beamLines.value = []
      return
    }

    // Read all card positions in one pass
    const cardRects = cardElements.map(card => card.getBoundingClientRect())

    // ============================================
    // PHASE 2: PERFORM ALL CALCULATIONS
    // ============================================
    const svgWidth = theaterRect.width
    const svgHeight = theaterRect.height

    // Calculate actual visual center of orb (including transforms from pulse animation)
    const originX = orbRect.left - theaterRect.left + orbRect.width / 2
    const originY = orbRect.top - theaterRect.top + orbRect.height / 2

    // Calculate all beam positions
    const calculatedBeams = cardRects.map(rect => {
      const cardCenterX = rect.left - theaterRect.left + rect.width / 2
      const cardCenterY = rect.top - theaterRect.top + rect.height / 2

      return {
        x1: originX,
        y1: originY,
        x2: cardCenterX,
        y2: cardCenterY
      }
    })

    // ============================================
    // PHASE 3: BATCH ALL WRITES (triggers single reflow)
    // ============================================
    svgSize.value = { width: svgWidth, height: svgHeight }
    beamLines.value = calculatedBeams
  },
  { immediate: false }
)

