      easing: EASING.ELASTIC,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  rotate45: {
    keyframes: [
      { transform: 'rotate(0deg)' },
      { transform: 'rotate(45deg)' }
    ],
    options: {
      duration: ANIMATION_DURATION.FAST,
      easing: EASING.SMOOTH,
