/**
 * Minimal type definitions for floating panels
 * Extracted from legacy composables to maintain compatibility
 */

export interface FloatingPanelPosition {
  x: number;
  y: number;
}

export interface FloatingPanelSize {
  width: number;
  height: number;
}

export interface FloatingPanelAnimationPreset {
  keyframes: any; // UseAnimateKeyframes from @vueuse/core
  options: any; // UseAnimateOptions from @vueuse/core
  mobile?: {
    keyframes?: any;
    options?: Partial<any>;
  };
}

export interface FloatingPanelMorphConfig {
  duration: number;
  easing: string;
  springDamping?: number;
  springStiffness?: number;
  enableSizeSmoothing?: boolean;
  enableAspectRatioTransition?: boolean;
}

// Re-export for backward compatibility
export const floatingPanelAnimationPresets: Record<string, FloatingPanelAnimationPreset> = {
  // Panel entrance animations
  slideScaleFadeIn: {
    keyframes: [
      {
        opacity: 0,
        transform: 'translate3d(0, 20px, 0) scale(0.9)',
        filter: 'blur(4px)'
      },
      {
        opacity: 0.5,
        transform: 'translate3d(0, 10px, 0) scale(0.95)',
        filter: 'blur(2px)'
      },
      {
        opacity: 1,
        transform: 'translate3d(0, 0, 0) scale(1)',
        filter: 'blur(0)'
      }
    ],
    options: {
      duration: 400,
      easing: 'cubic-bezier(0.34, 1.56, 0.64, 1)',
      fill: 'forwards'
    },
    mobile: {
      options: {
        duration: 250,
        easing: 'cubic-bezier(0.25, 0.46, 0.45, 0.94)'
      }
    }
  },

  elasticEnter: {
    keyframes: [
      {
        opacity: 0,
        transform: 'translate3d(0, 30px, 0) scale(0.7) rotate(-2deg)'
      },
      {
        opacity: 0.3,
        transform: 'translate3d(0, -5px, 0) scale(1.05) rotate(1deg)'
      },
      {
        opacity: 0.8,
        transform: 'translate3d(0, 2px, 0) scale(0.98) rotate(-0.5deg)'
      },
      {
        opacity: 1,
        transform: 'translate3d(0, 0, 0) scale(1) rotate(0deg)'
      }
    ],
    options: {
      duration: 600,
      easing: 'cubic-bezier(0.68, -0.55, 0.265, 1.55)',
      fill: 'forwards'
    },
    mobile: {
      options: {
        duration: 350,
        easing: 'cubic-bezier(0.34, 1.56, 0.64, 1)'
      }
    }
  },

  collapseOut: {
    keyframes: [
      {
        opacity: 1,
        transform: 'translate3d(0, 0, 0) scale(1)'
      },
      {
        opacity: 0.5,
        transform: 'translate3d(0, 10px, 0) scale(0.95)'
      },
      {
        opacity: 0,
        transform: 'translate3d(0, 20px, 0) scale(0.8)'
      }
    ],
    options: {
      duration: 300,
      easing: 'cubic-bezier(0.4, 0, 0.6, 1)',
      fill: 'forwards'
    }
  },

  'fade-in': {
    keyframes: [
      { opacity: 0 },
      { opacity: 1 }
    ],
    options: {
      duration: 200,
      easing: 'cubic-bezier(0.4, 0, 0.2, 1)',
      fill: 'forwards'
    }
  },

  'fade-out': {
    keyframes: [
      { opacity: 1 },
      { opacity: 0 }
    ],
    options: {
      duration: 200,
      easing: 'cubic-bezier(0.4, 0, 0.2, 1)',
      fill: 'forwards'
    }
  },

  slideScaleFadeOut: {
    keyframes: [
      {
        opacity: 1,
        transform: 'translate3d(0, 0, 0) scale(1)',
        filter: 'blur(0)'
      },
      {
        opacity: 0.5,
        transform: 'translate3d(0, 10px, 0) scale(0.95)',
        filter: 'blur(2px)'
      },
      {
        opacity: 0,
        transform: 'translate3d(0, 20px, 0) scale(0.9)',
        filter: 'blur(4px)'
      }
    ],
    options: {
      duration: 300,
      easing: 'cubic-bezier(0.4, 0, 0.6, 1)',
      fill: 'forwards'
    }
  }
};

