  --color-gray-950: oklch(10% 0 0);

  /* ===== SPACING SYSTEM ===== */
  /* Tailwind default scale works for most cases
     Only add custom if you have specific design needs */

  --spacing-18: 4.5rem;  /* Example: Custom size between 16 and 20 */

  /* ===== TYPOGRAPHY ===== */

  --font-sans: 'Inter', ui-sans-serif, system-ui, sans-serif;
  --font-mono: 'JetBrains Mono', ui-monospace, monospace;

  /* Only customize if you need non-standard sizes */
  --font-size-display: clamp(3rem, 5vw, 6rem);

