   440→  position: relative;
   441→}
   442→
   443→.bg-gradient-mesh-overlay::before {
   444→  content: '';
   445→  position: absolute;
   446→  inset: 0;
   447→  background: var(--gradient-mesh-overlay);
   448→  pointer-events: none;
   449→  z-index: 1;
   450→}
   451→
   452→/* Text Gradients - utility classes for gradient text */
   453→.text-gradient-primary {
   454→  background: var(--gradient-accent-primary);
   455→  -webkit-background-clip: text;
   456→  background-clip: text;
   457→  -webkit-text-fill-color: transparent;
   458→  color: transparent; /* Fallback */
   459→}
   460→
   461→.text-gradient-purple {
   462→  background: var(--gradient-accent-purple);
