@import "tailwindcss";

/* Define cascade layer order - explicit precedence */
@layer reset, theme, composition, utilities, components, overrides;

/* Import style files into appropriate layers */
@import "./keyframes.css" layer(theme);
@import "./typography.css" layer(components);
@import "./components.css" layer(components);
@import "./utilities.css" layer(utilities);

/* Dark mode variant */
@custom-variant dark (&:where([data-theme=dark], [data-theme=dark] *));

/* Accessibility-focused custom variants */
@custom-variant motion-safe {
  @media (prefers-reduced-motion: no-preference) {
    @slot;
  }
}

@custom-variant motion-reduce {
  @media (prefers-reduced-motion: reduce) {
    @slot;
  }
}

@custom-variant contrast-high {
  @media (prefers-contrast: high) {
    @slot;
  }
}

@custom-variant touch {
  @media (pointer: coarse) {
    @slot;
  }
}

@custom-variant mouse {
  @media (pointer: fine) {
    @slot;
  }
}

@custom-variant hover-supported {
  @media (hover: hover) {
    @slot;
  }
}

/* ============================================
   TAILWIND V4 DESIGN TOKENS
   Define ONCE here - generates Tailwind utilities
   ============================================ */

@theme {
  /* ===== BRAND COLORS ===== */

  /* Primary (Teal) - Full scale for flexibility */
  --color-primary-50: oklch(95% 0.03 200);
  --color-primary-100: oklch(90% 0.05 200);
  --color-primary-200: oklch(80% 0.08 200);
  --color-primary-300: oklch(70% 0.11 200);
  --color-primary-400: oklch(60% 0.14 200);
  --color-primary-500: oklch(50% 0.16 200);  /* Base: #007c89 */
  --color-primary-600: oklch(45% 0.14 200);
  --color-primary-700: oklch(35% 0.11 200);
  --color-primary-800: oklch(25% 0.08 200);
  --color-primary-900: oklch(15% 0.05 200);
  --color-primary-950: oklch(10% 0.03 200);

  /* ===== SEMANTIC COLORS ===== */

  --color-success: oklch(65% 0.17 160);  /* Green */
  --color-warning: oklch(75% 0.18 85);   /* Amber */
  --color-error: oklch(60% 0.22 25);     /* Red */
  --color-info: oklch(65% 0.18 250);     /* Blue */

  /* ===== NEUTRAL GRAYS ===== */

  --color-gray-50: oklch(98% 0 0);
  --color-gray-100: oklch(96% 0 0);
  --color-gray-200: oklch(90% 0 0);
  --color-gray-300: oklch(83% 0 0);
  --color-gray-400: oklch(64% 0 0);
  --color-gray-500: oklch(53% 0 0);
  --color-gray-600: oklch(45% 0 0);
  --color-gray-700: oklch(38% 0 0);
  --color-gray-800: oklch(27% 0 0);
  --color-gray-900: oklch(15% 0 0);
  --color-gray-950: oklch(10% 0 0);

  /* ===== TYPOGRAPHY ===== */

  --font-sans: 'Inter', ui-sans-serif, system-ui, -apple-system, sans-serif;
  --font-mono: 'JetBrains Mono', ui-monospace, 'SF Mono', monospace;

  /* Custom font size for hero sections */
  --font-size-display: clamp(3rem, 5vw + 1rem, 6rem);

  /* ===== CUSTOM EFFECTS ===== */

  /* Brand-specific shadows */
  --shadow-glow: 0 0 20px rgb(0 124 137 / 0.3);
  --shadow-glow-lg: 0 0 40px rgb(0 124 137 / 0.4);

  /* Brutal/Neo-brutalism style */
  --shadow-brutal: 4px 4px 0 0 rgb(0 0 0 / 1);

  /* ===== ANIMATION TOKENS ===== */

  /* Custom easing curves */
  --ease-smooth: cubic-bezier(0.16, 1, 0.3, 1);
  --ease-bounce: cubic-bezier(0.68, -0.55, 0.265, 1.55);
  --ease-elastic: cubic-bezier(0.68, -0.55, 0.265, 1.55);

  /* Durations - synced with constants/animations.ts */
  --duration-instant: 100ms;
  --duration-fast: 200ms;
  --duration-normal: 300ms;
  --duration-slow: 500ms;
  --duration-slower: 800ms;
  --duration-celebration: 2000ms;

  /* ===== Z-INDEX SCALE ===== */

  --z-dropdown: 1000;
  --z-sticky: 1020;
  --z-modal: 1050;
  --z-tooltip: 1070;
  --z-toast: 1080;
}

/* ===== DARK MODE OVERRIDES ===== */

@theme dark {
  /* Adjust primary color slightly lighter in dark mode for better visibility */
  --color-primary-500: oklch(55% 0.18 200);
}

/* ============================================
   UTILITY CLASSES
   ============================================ */

.label-position {
  top: min(min(-1.5vh, -1.5vw), -1.5rem);
}

/* ===== COMPONENT LAYER: Reusable patterns ===== */
@layer components {
  /* Prose styles for documentation */
  .prose {
    @apply text-gray-900 dark:text-gray-100;
  }

  .prose h1,
  .prose h2,
  .prose h3,
  .prose h4,
  .prose h5,
  .prose h6 {
    @apply text-gray-900 dark:text-white font-semibold;
  }

  .prose h1 {
    @apply text-4xl sm:text-5xl leading-tight mt-12 mb-6;
  }

  .prose h2 {
    @apply text-3xl sm:text-4xl leading-snug mt-10 mb-5;
  }

  .prose h3 {
    @apply text-2xl sm:text-3xl leading-snug mt-8 mb-4;
  }

  .prose h4 {
    @apply text-xl sm:text-2xl leading-snug mt-6 mb-3;
  }

  .prose h5 {
    @apply text-lg leading-snug mt-5 mb-2;
  }

  .prose h6 {
    @apply text-base uppercase tracking-wide text-gray-600 dark:text-gray-400 mt-4 mb-2;
  }

  .prose a {
    @apply text-primary-600 dark:text-primary-400 hover:text-primary-700 dark:hover:text-primary-300 underline decoration-primary-300 underline-offset-2 transition-colors;
  }

  .prose p {
    @apply text-gray-800 dark:text-gray-200 leading-relaxed mt-4 mb-4;
  }

  .prose code {
    @apply bg-gray-100 dark:bg-gray-800 text-primary-700 dark:text-primary-300 px-1.5 py-0.5 rounded font-mono text-sm;
  }

  .prose pre {
    @apply bg-gray-100 dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-lg p-4 my-6 overflow-x-auto;
  }

  .prose pre code {
    @apply bg-transparent text-gray-900 dark:text-gray-100 px-0 py-0;
  }

  .prose strong {
    @apply text-gray-900 dark:text-white font-semibold;
  }

  .prose em {
    @apply text-gray-700 dark:text-gray-200;
  }

  .prose blockquote {
    @apply border-l-4 border-l-primary-500 dark:border-l-primary-400 text-gray-700 dark:text-gray-300 bg-gray-50 dark:bg-gray-800/50 px-5 py-4 rounded-r italic my-6;
  }

  .prose ul,
  .prose ol {
    @apply my-5 pl-6;
  }

  .prose ul {
    @apply list-disc;
  }

  .prose ol {
    @apply list-decimal;
  }

  .prose li {
    @apply my-2;
  }

  .prose ul li,
  .prose ol li {
    @apply text-gray-800 dark:text-gray-200;
  }

  .prose ul li::marker,
  .prose ol li::marker {
    @apply text-primary-600 dark:text-primary-400;
  }

  .prose hr {
    @apply border-gray-300 dark:border-gray-700 my-10;
  }

  /* Table styles for documentation */
  .prose table {
    @apply w-full border-collapse my-6;
  }

  .prose thead {
    @apply border-b-2 border-gray-300 dark:border-gray-700;
  }

  .prose th {
    @apply px-4 py-2 text-left font-semibold text-gray-900 dark:text-white;
  }

  .prose td {
    @apply px-4 py-2 border-t border-gray-200 dark:border-gray-700;
  }

  .prose tbody tr:hover {
    @apply bg-gray-50 dark:bg-gray-800;
  }

  .prose img,
  .prose video,
  .prose picture {
    @apply my-6 rounded-lg shadow-sm;
  }
} /* End @layer components */

/* ===== USAGE EXAMPLES =====

Tailwind utilities now available:
  bg-primary-500       ← --color-primary-500
  text-success         ← --color-success
  text-gray-600        ← --color-gray-600

  shadow-glow          ← --shadow-glow
  shadow-brutal        ← --shadow-brutal

  ease-smooth          ← --ease-smooth
  duration-normal      ← --duration-normal

  z-modal              ← --z-modal

Dark mode:
  dark:bg-gray-900
  dark:text-primary-400

*/
