// Animation handling for pricing cards
const hasBeenVisible = ref<boolean[]>(Array(4).fill(false)); // 3 main tiers + 1 enterprise

// Set initial visibility after a short delay for a nice entrance effect
setTimeout(() => {
  hasBeenVisible.value = Array(4).fill(true);
}, 100);

// Helper functions to organize tiers for display
const getMainTiers = (): TransformedTier[] => {
  if (!pricingData.value.tiers) return [];

  // Filter out enterprise tier and get the 3 main tiers
  const nonEnterpriseTiers = pricingData.value.tiers.filter(tier => 
    tier.billingKey !== 'enterprise'
  );

  // Sort by price (free first, then ascending by price)
  return nonEnterpriseTiers.sort((a, b) => {
    // Free tier always comes first
    if (a.billingKey === 'free') return -1;
    if (b.billingKey === 'free') return 1;

    // Sort others by monthly price
    return a.monthlyPrice - b.monthlyPrice;
  }).slice(0, 3); // Take only the first 3 tiers
};

// Fetch data on mount
onMounted(() => {
  fetchPricingTiers();
});
</script>

<style scoped>
/* Pricing card entrance animations */
.pricing-card {
  opacity: 0;
  transform: translateY(30px);
  transition: opacity 0.6s cubic-bezier(0.4, 0, 0.2, 1), transform 0.6s cubic-bezier(0.4, 0, 0.2, 1);
}

.pricing-visible {
  opacity: 1;
  transform: translateY(0);
}

/* Staggered animation delays */
.pricing-card:nth-child(1) { transition-delay: 100ms; }
.pricing-card:nth-child(2) { transition-delay: 200ms; }
.pricing-card:nth-child(3) { transition-delay: 300ms; }

