const transformUnifiedTier = (tier: any): TransformedTier => {
  console.log("Transforming tier:", tier);
  // Generate features list from tier attributes
  const features: string[] = [];

  // Connections
  if (tier.connectionsMax === null) {
    features.push("Unlimited Connected Accounts");
  } else {
    features.push(`Up to ${tier.connectionsMax} Connected Accounts`);
  }

  // Teams
  features.push(`${tier.teamsMax} Team${tier.teamsMax > 1 ? 's' : ''}`);

  // Users
  features.push(`${tier.usersMax} User${tier.usersMax > 1 ? 's' : ''}`);

  // Storage
  if (tier.storageMax >= 1024) {
    const gb = Math.floor(tier.storageMax / 1024);
    const remaining = tier.storageMax % 1024;
    if (remaining === 0) {
      features.push(`${gb} GB Storage Limit`);
    } else {
      features.push(`${gb}.${Math.round(remaining / 102.4)} GB Storage Limit`);
    }
  } else {
    features.push(`${tier.storageMax} MB Storage Limit`);
  }

  // AI Credits
  if (tier.aiCreditsMax > 0) {
    features.push(`${tier.aiCreditsMax} AI Credits`);
  }

  // Executions
  if (tier.unlimitedExecutions) {
    features.push("Unlimited Workflow Executions");
  }

  // Additional features for higher tiers
  if (tier.billingKey !== 'free') {
    features.push("Basic Analytics");
    features.push("24/7 Automated Support");
  }

  if (tier.billingKey === 'creator' || tier.billingKey === 'agency' || tier.billingKey === 'enterprise') {
    features.push("Priority Support");
  }

  if (tier.billingKey === 'agency' || tier.billingKey === 'enterprise') {
    features.push("Advanced Analytics");
  }

  // Generate limits object for backwards compatibility
  const limits = {
    connections: tier.connectionsMax,
    teams: tier.teamsMax,
    users: tier.usersMax,
    storage: tier.storageMax,
    aiCredits: tier.aiCreditsMax,
    chatEnabled: tier.chatEnabled,
    encodingEnabled: tier.encodingEnabled,
    unlimited: tier.unlimitedExecutions,
  };

  return {
    name: tier.name,
    description: tier.description,
    monthlyPrice: tier.monthlyPrice,
    yearlyPrice: tier.yearlyPrice,
    features: features,
    limits: limits,
    billingKey: tier.billingKey,
    buttonText: tier.buttonText || "Get Started",
    isPopular: tier.isPopular || false,
  };
};

// Computed pricing data from unified pricing tiers
const pricingData = computed(() => {
  console.log("pricingData computed - unifiedPricingTiers.value:", unifiedPricingTiers.value);

  if (!unifiedPricingTiers.value || unifiedPricingTiers.value.length === 0) {
    console.warn("No unified pricing tiers available");
    return {
      title: "Simple, Transparent Pricing",
      subtitle: "Start free, upgrade as you grow. No hidden fees, cancel anytime.",
      tiers: [] as TransformedTier[],
    };
  }

  const transformedTiers: TransformedTier[] = unifiedPricingTiers.value.map(transformUnifiedTier);
  console.log("Transformed tiers:", transformedTiers);

  return {
    title: "Simple, Transparent Pricing",
    subtitle: "Start free, upgrade as you grow. No hidden fees, cancel anytime.",
    tiers: transformedTiers,

