// Helper functions to organize tiers for display
const getMainTiers = (): TransformedTier[] => {
  if (!pricingData.value.tiers) return [];

  // Filter out enterprise tier and get the 3 main tiers
  const nonEnterpriseTiers = pricingData.value.tiers.filter(tier =>
    tier.billingKey !== 'enterprise'
  );

  // Sort by price (free first, then ascending by price)
  const sortedTiers = nonEnterpriseTiers.sort((a, b) => {
    // Free tier always comes first
    if (a.billingKey === 'free') return -1;
    if (b.billingKey === 'free') return 1;

    // Sort others by monthly price
    return a.monthlyPrice - b.monthlyPrice;
  }).slice(0, 3); // Take only the first 3 tiers

  // Ensure the middle card (index 1) is marked as popular
  if (sortedTiers.length >= 2) {
    sortedTiers[1].isPopular = true;
  }

  console.log("Final sorted tiers with isPopular:", sortedTiers.map(t => ({ name: t.name, isPopular: t.isPopular })));

  return sortedTiers;
};
