content.content:
     1→/**
     2→ * Socialaize Design System Foundation
     3→ * 
     4→ * A comprehensive design token system for consistent theming,
     5→ * components, and user experiences across the platform.
     6→ */
     7→
     8→:root {
     9→  /* ===== COLOR PALETTE ===== */
    10→
    11→  /* Primary Brand Colors - OKLCH for perceptual uniformity and better dark mode */
    12→  --color-primary-50: oklch(95% 0.03 200);
    13→  --color-primary-100: oklch(90% 0.05 200);
    14→  --color-primary-200: oklch(80% 0.08 200);
    15→  --color-primary-300: oklch(70% 0.11 200);
    16→  --color-primary-400: oklch(60% 0.14 200);
    17→  --color-primary-500: oklch(50% 0.16 200);  /* Base: #007c89 */
    18→  --color-primary-600: oklch(45% 0.14 200);
    19→  --color-primary-700: oklch(35% 0.11 200);
    20→  --color-primary-800: oklch(25% 0.08 200);
    21→  --color-primary-900: oklch(15% 0.05 200);
    22→  --color-primary-950: oklch(10% 0.03 200);
    23→
    24→  /* Semantic Colors - Success (OKLCH) */
    25→  --color-success-50: oklch(98% 0.03 160);
    26→  --color-success-100: oklch(95% 0.05 160);
    27→  --color-success-200: oklch(90% 0.08 160);
    28→  --color-success-300: oklch(82% 0.12 160);
    29→  --color-success-400: oklch(72% 0.15 160);
    30→  --color-success-500: oklch(65% 0.17 160);  /* Base green */
    31→  --color-success-600: oklch(58% 0.15 160);
    32→  --color-success-700: oklch(50% 0.13 160);
    33→  --color-success-800: oklch(42% 0.10 160);
    34→  --color-success-900: oklch(35% 0.08 160);
    35→  --color-success-950: oklch(22% 0.05 160);
    36→
    37→  /* Semantic Colors - Warning (OKLCH) */
    38→  --color-warning-50: oklch(99% 0.02 85);
    39→  --color-warning-100: oklch(97% 0.05 85);
    40→  --color-warning-200: oklch(93% 0.10 85);
    41→  --color-warning-300: oklch(88% 0.14 85);
    42→  --color-warning-400: oklch(82% 0.16 85);
    43→  --color-warning-500: oklch(75% 0.18 85);  /* Base amber */
    44→  --color-warning-600: oklch(68% 0.17 85);
    45→  --color-warning-700: oklch(58% 0.15 85);
    46→  --color-warning-800: oklch(48% 0.12 85);
    47→  --color-warning-900: oklch(42% 0.10 85);
    48→  --color-warning-950: oklch(30% 0.07 85);
    49→
    50→  /* Semantic Colors - Error/Danger (OKLCH) */
    51→  --color-error-50: oklch(98% 0.02 25);
    52→  --color-error-100: oklch(96% 0.04 25);
    53→  --color-error-200: oklch(92% 0.08 25);
    54→  --color-error-300: oklch(85% 0.12 25);
    55→  --color-error-400: oklch(75% 0.16 25);
    56→  --color-error-500: oklch(62% 0.22 25);  /* Base red */
    57→  --color-error-600: oklch(55% 0.22 25);
    58→  --color-error-700: oklch(48% 0.19 25);
    59→  --color-error-800: oklch(42% 0.15 25);
    60→  --color-error-900: oklch(38% 0.12 25);
    61→  --color-error-950: oklch(25% 0.08 25);
    62→
    63→  /* Semantic Colors - Info (OKLCH) */
    64→  --color-info-50: oklch(97% 0.02 250);
    65→  --color-info-100: oklch(94% 0.04 250);
    66→  --color-info-200: oklch(88% 0.08 250);
    67→  --color-info-300: oklch(80% 0.12 250);
    68→  --color-info-400: oklch(70% 0.16 250);
    69→  --color-info-500: oklch(60% 0.20 250);  /* Base blue */
    70→  --color-info-600: oklch(52% 0.20 250);
    71→  --color-info-700: oklch(48% 0.18 250);
