/**
 * Socialaize Design System Foundation
 * 
 * A comprehensive design token system for consistent theming,
 * components, and user experiences across the platform.
 */

:root {
  /* ===== COLOR PALETTE ===== */

  /* Primary Brand Colors - OKLCH for perceptual uniformity and better dark mode */
  --color-primary-50: oklch(95% 0.03 200);
  --color-primary-100: oklch(90% 0.05 200);
  --color-primary-200: oklch(80% 0.08 200);
  --color-primary-300: oklch(70% 0.11 200);
  --color-primary-400: oklch(60% 0.14 200);
  --color-primary-500: oklch(50% 0.16 200);  /* Base: #007c89 */
  --color-primary-600: oklch(45% 0.14 200);
  --color-primary-700: oklch(35% 0.11 200);
  --color-primary-800: oklch(25% 0.08 200);
  --color-primary-900: oklch(15% 0.05 200);
  --color-primary-950: oklch(10% 0.03 200);

  /* Semantic Colors - Success (OKLCH) */
  --color-success-50: oklch(98% 0.03 160);
  --color-success-100: oklch(95% 0.05 160);
  --color-success-200: oklch(90% 0.08 160);
  --color-success-300: oklch(82% 0.12 160);
  --color-success-400: oklch(72% 0.15 160);
  --color-success-500: oklch(65% 0.17 160);  /* Base green */
  --color-success-600: oklch(58% 0.15 160);
  --color-success-700: oklch(50% 0.13 160);
  --color-success-800: oklch(42% 0.10 160);
  --color-success-900: oklch(35% 0.08 160);
  --color-success-950: oklch(22% 0.05 160);

  /* Semantic Colors - Warning (OKLCH) */
  --color-warning-50: oklch(99% 0.02 85);
  --color-warning-100: oklch(97% 0.05 85);
  --color-warning-200: oklch(93% 0.10 85);
  --color-warning-300: oklch(88% 0.14 85);
  --color-warning-400: oklch(82% 0.16 85);
  --color-warning-500: oklch(75% 0.18 85);  /* Base amber */
  --color-warning-600: oklch(68% 0.17 85);
  --color-warning-700: oklch(58% 0.15 85);
  --color-warning-800: oklch(48% 0.12 85);
  --color-warning-900: oklch(42% 0.10 85);
  --color-warning-950: oklch(30% 0.07 85);

  /* Semantic Colors - Error/Danger (OKLCH) */
  --color-error-50: oklch(98% 0.02 25);
  --color-error-100: oklch(96% 0.04 25);
  --color-error-200: oklch(92% 0.08 25);
  --color-error-300: oklch(85% 0.12 25);
  --color-error-400: oklch(75% 0.16 25);
  --color-error-500: oklch(62% 0.22 25);  /* Base red */
  --color-error-600: oklch(55% 0.22 25);
  --color-error-700: oklch(48% 0.19 25);
  --color-error-800: oklch(42% 0.15 25);
  --color-error-900: oklch(38% 0.12 25);
  --color-error-950: oklch(25% 0.08 25);

  /* Semantic Colors - Info (OKLCH) */
  --color-info-50: oklch(97% 0.02 250);
  --color-info-100: oklch(94% 0.04 250);
  --color-info-200: oklch(88% 0.08 250);
  --color-info-300: oklch(80% 0.12 250);
  --color-info-400: oklch(70% 0.16 250);
  --color-info-500: oklch(60% 0.20 250);  /* Base blue */
  --color-info-600: oklch(52% 0.20 250);
  --color-info-700: oklch(48% 0.18 250);
  --color-info-800: oklch(42% 0.15 250);
  --color-info-900: oklch(38% 0.12 250);
  --color-info-950: oklch(28% 0.08 250);

  /* Neutral Grays */
  --color-gray-50: #f9fafb;
  --color-gray-100: #f3f4f6;
  --color-gray-200: #e5e7eb;
  --color-gray-300: #d1d5db;
  --color-gray-400: #9ca3af;
  --color-gray-500: #6b7280;
  --color-gray-600: #4b5563;
  --color-gray-700: #374151;
  --color-gray-800: #1f2937;
  --color-gray-900: #111827;
  --color-gray-950: #030712;

  /* Surface Colors */
  --color-surface-primary: #ffffff;
  --color-surface-secondary: var(--color-gray-50);
  --color-surface-tertiary: var(--color-gray-100);
  --color-surface-inverse: var(--color-gray-900);

  /* Border Colors */
  --color-border-primary: var(--color-gray-200);
  --color-border-secondary: var(--color-gray-300);
  --color-border-focus: var(--color-primary-500);
  --color-border-error: var(--color-error-500);
  --color-border-success: var(--color-success-500);

