/**
 * Component Layer Styles (Converted from Tailwind Plugins)
 *
 * This file contains component base styles that were previously defined
 * as JavaScript plugins. Converted to pure CSS for Tailwind v4 best practices.
 *
 * Includes:
 * - Form controls (.form-control-base)
 * - Buttons (.btn-base, .btn-sm, .btn-md, .btn-lg, .btn-xl)
 * - Cards (.card-base, .card-interactive)
 * - Badges (.badge-base)
 * - Modals (.modal-backdrop, .modal-panel)
 * - Input groups (.input-group)
 */

@layer components {
  /* ==========================================
   * FORM CONTROL BASE
   * ========================================== */
  .form-control-base {
    width: 100%;
    padding: var(--spacing-2) var(--spacing-4);
    border: 1px solid var(--color-border-primary, var(--color-neutral-300));
    border-radius: var(--radius-lg);
    background-color: var(--color-surface-primary, #ffffff);
    color: var(--color-neutral-900);
    font-size: var(--font-size-base);
    line-height: var(--line-height-normal);
    transition: border-color 150ms ease, box-shadow 150ms ease;
  }

  .form-control-base::placeholder {
    color: var(--color-neutral-400);
  }

  .form-control-base:focus {
    outline: none;
    border-color: var(--color-border-focus, var(--color-primary-500));
    box-shadow: var(--shadow-focus-primary);
  }

  .form-control-base:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    background-color: var(--color-neutral-100);
    color: var(--color-neutral-500);
  }

  .form-control-base[aria-invalid="true"] {
    border-color: var(--color-border-error, var(--color-error-500));
  }

  .form-control-base[aria-invalid="true"]:focus {
    box-shadow: var(--shadow-focus-error);
  }

  /* Dark mode support */
  [data-theme="dark"] .form-control-base {
    background-color: var(--color-dark-surface-primary);
    border-color: var(--color-dark-border-primary);
    color: var(--color-dark-text-primary);
  }

  [data-theme="dark"] .form-control-base::placeholder {
    color: var(--color-neutral-500);
  }

  [data-theme="dark"] .form-control-base:disabled {
    background-color: var(--color-dark-surface-secondary);
    color: var(--color-neutral-600);
  }

  /* ==========================================
   * BUTTON BASE
   * ========================================== */
  .btn-base {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: var(--spacing-2);
    font-weight: var(--font-weight-medium);
    border-radius: var(--radius-lg);
    transition: all 150ms ease;
    cursor: pointer;
    text-decoration: none;
    white-space: nowrap;
    user-select: none;
  }

  .btn-base:disabled,
  .btn-base[aria-disabled="true"] {
    opacity: 0.6;
    pointer-events: none;
    cursor: not-allowed;
  }

  .btn-base:focus-visible {
    outline: none;
    box-shadow: var(--shadow-focus-primary);
  }

