import { ref, reactive, computed, watch, onMounted, nextTick, readonly } from 'vue';

// TypeScript interfaces
export interface OnboardingStep {
  id: string;
  title: string;
  description: string;
  target: string; // CSS selector
  position: 'top' | 'bottom' | 'left' | 'right' | 'center';
  action?: {
    type: 'click' | 'hover' | 'input' | 'scroll';
    element?: string;
    value?: string;
  };
  condition?: () => boolean;
  completed: boolean;
  skippable: boolean;
  highlight: boolean;
}

export interface OnboardingTour {
  id: string;
  name: string;
  description: string;
  steps: OnboardingStep[];
  isActive: boolean;
  currentStepIndex: number;
  completedAt?: Date;
  version: string;
}

export interface OnboardingState {
  isActive: boolean;
  currentTour: OnboardingTour | null;
  availableTours: OnboardingTour[];
  completedTours: string[];
  userProgress: Record<string, any>;
  showTooltips: boolean;
  enableAnimations: boolean;
}

export const useOnboarding = () => {
  // Reactive state
  const state = reactive<OnboardingState>({
    isActive: false,
    currentTour: null,
    availableTours: [],
    completedTours: [],
    userProgress: {},
    showTooltips: true,
    enableAnimations: true
  });

  const overlayElement = ref<HTMLElement | null>(null);
  const tooltipElement = ref<HTMLElement | null>(null);

  // Tour definitions
  const defaultTours: OnboardingTour[] = [
    {
      id: 'real-time-features',
      name: 'Real-time Features Tour',
      description: 'Discover the new real-time capabilities and AI-powered features',
      version: '1.0.0',
      isActive: false,
      currentStepIndex: 0,
      steps: [
        {
          id: 'notifications',
          title: 'Smart Notifications',
          description: 'Your notifications now use AI to prioritize important alerts and group related messages.',
          target: '.notifications-container',
          position: 'left',
          completed: false,
          skippable: true,
          highlight: true
        },
        {
          id: 'connection-status',
          title: 'Real-time Connection',
          description: 'See your connection status and get updates instantly when your data changes.',
          target: '.connection-indicator',
          position: 'bottom',
          completed: false,
          skippable: true,
          highlight: true
        },
        {
          id: 'ai-chat',
          title: 'AI Assistant',
          description: 'Chat with your AI assistant for content creation, analytics insights, and workflow optimization.',
          target: '[title="AI Assistant"]',
          position: 'right',
          action: {
            type: 'click',
            element: '[title="AI Assistant"]'
          },
          completed: false,
          skippable: true,
          highlight: true
        },
        {
          id: 'accessibility',
          title: 'Accessibility Features',
          description: 'Press Ctrl+K for chat, Ctrl+N for notifications, and Ctrl+/ for search. Full keyboard navigation is available.',
          target: 'body',
          position: 'center',
          completed: false,
          skippable: true,
          highlight: false
        },
        {
          id: 'mobile-optimization',
          title: 'Mobile Experience',
          description: 'Enjoy optimized touch gestures, PWA capabilities, and battery-conscious performance on mobile devices.',
          target: 'body',
          position: 'center',
          condition: () => window.innerWidth <= 768,
          completed: false,
          skippable: true,
          highlight: false
        }
      ]
    },
    {
      id: 'advanced-ai',
      name: 'Advanced AI Features',
      description: 'Learn about the enhanced AI capabilities for content creation and analytics',
      version: '1.0.0',
      isActive: false,
      currentStepIndex: 0,
      steps: [
        {
          id: 'context-analysis',
          title: 'Context-Aware Suggestions',
          description: 'The AI now analyzes your current dashboard context to provide relevant suggestions.',
          target: '.chat-widget-container',
          position: 'left',
          completed: false,
          skippable: true,
          highlight: true
        },
        {
          id: 'predictive-text',
          title: 'Predictive Text',
          description: 'Get intelligent text completion suggestions while creating posts.',
          target: '.create-post-form',
          position: 'top',
          condition: () => window.location.pathname.includes('/posts/create'),
          completed: false,
          skippable: true,
          highlight: true
        },
        {
          id: 'smart-scheduling',
          title: 'Smart Scheduling',
          description: 'AI analyzes your audience data to recommend optimal posting times.',
          target: '.scheduling-section',
          position: 'right',
          condition: () => window.location.pathname.includes('/posts/create'),
          completed: false,
          skippable: true,
          highlight: true
        },
        {
          id: 'analytics-insights',
          title: 'Natural Language Analytics',
          description: 'Ask questions about your analytics in plain English and get detailed insights.',
          target: '.analytics-dashboard',
          position: 'top',
          condition: () => window.location.pathname.includes('/analytics'),
          completed: false,
          skippable: true,
          highlight: true
        }
      ]
    }
  ];

  // Computed properties
  const currentStep = computed(() => {
    if (!state.currentTour) return null;
    return state.currentTour.steps[state.currentTour.currentStepIndex] || null;
  });

  const isLastStep = computed(() => {
    if (!state.currentTour) return false;
    return state.currentTour.currentStepIndex >= state.currentTour.steps.length - 1;
  });

  const isFirstStep = computed(() => {
    if (!state.currentTour) return false;
    return state.currentTour.currentStepIndex === 0;
  });

  const tourProgress = computed(() => {
    if (!state.currentTour) return 0;
    return Math.round((state.currentTour.currentStepIndex / state.currentTour.steps.length) * 100);
  });

  const availableToursForUser = computed(() => {
    return state.availableTours.filter(tour => {
      // Don't show completed tours unless they have new versions
      const wasCompleted = state.completedTours.includes(tour.id);
      if (wasCompleted) {
        // Check if there's a newer version
        const completedVersion = state.userProgress[tour.id]?.version;
        return tour.version !== completedVersion;
      }
      return true;
    });
  });

  // Initialize with default tours
  const initializeDefaultTours = (): void => {
    state.availableTours = [...defaultTours];
    console.log('🎯 Onboarding tours initialized:', state.availableTours.length);
  };

  // Start a tour
  const startTour = (tourId: string): void => {
    const tour = state.availableTours.find(t => t.id === tourId);
    if (!tour) {
      console.warn(`Tour ${tourId} not found`);
      return;
    }

    state.currentTour = { ...tour };
    state.currentTour.currentStepIndex = 0;
    state.currentTour.isActive = true;
    state.isActive = true;

    console.log(`🎯 Starting tour: ${tour.name}`);

    nextTick(() => {
      showCurrentStep();
    });
  };

  // Show current step
  const showCurrentStep = (): void => {
    if (!currentStep.value) return;

    const step = currentStep.value;

    // Check condition if present
    if (step.condition && !step.condition()) {
      console.log(`Skipping step ${step.id} - condition not met`);
      nextStep();
      return;
    }

    // Find target element
    const targetElement = document.querySelector(step.target) as HTMLElement;
    if (!targetElement && step.target !== 'body') {
      console.warn(`Target element not found: ${step.target}`);
      nextStep();
      return;
    }

    // Create overlay and highlight
    createOverlay(targetElement, step);
    createTooltip(targetElement, step);

    // Announce to screen readers
    announceStep(step);
  };

  // Create overlay with highlight
  const createOverlay = (target: HTMLElement | null, step: OnboardingStep): void => {
    removeOverlay();

    if (!step.highlight) return;

    const overlay = document.createElement('div');
    overlay.className = 'onboarding-overlay';
    overlay.style.cssText = `
      position: fixed;
      top: 0;
      left: 0;
      width: 100vw;
      height: 100vh;
      background: rgba(0, 0, 0, 0.6);
      z-index: 9999;
      pointer-events: none;
      transition: opacity 0.3s ease;
    `;

    if (target && target !== document.body) {
      const rect = target.getBoundingClientRect();
      const highlightPadding = 8;

      // Create highlight cutout
      const cutout = document.createElement('div');
      cutout.style.cssText = `
        position: absolute;
        top: ${rect.top - highlightPadding}px;
        left: ${rect.left - highlightPadding}px;
        width: ${rect.width + highlightPadding * 2}px;
        height: ${rect.height + highlightPadding * 2}px;
        box-shadow: 0 0 0 9999px rgba(0, 0, 0, 0.6);
        border-radius: var(--radius-lg);
        pointer-events: auto;
      `;

      overlay.appendChild(cutout);
    }

    document.body.appendChild(overlay);
    overlayElement.value = overlay;

    // Add click handler to close
    overlay.addEventListener('click', (e) => {
      if (e.target === overlay) {
        skipTour();
      }
    });
  };

  // Create tooltip
  const createTooltip = (target: HTMLElement | null, step: OnboardingStep): void => {
    removeTooltip();

    const tooltip = document.createElement('div');
    tooltip.className = 'onboarding-tooltip';

    const isCenter = step.position === 'center';
    const isMobile = window.innerWidth <= 768;

    let tooltipStyles = `
      position: fixed;
      z-index: 10000;
      background: var(--color-surface-primary);
      border: 1px solid var(--color-border-primary);
      border-radius: var(--radius-xl);
      box-shadow: var(--shadow-xl);
      padding: var(--spacing-6);
      max-width: 320px;
      font-family: var(--font-family-sans);
      color: var(--color-text-primary);
    `;

    if (isCenter || isMobile) {
      tooltipStyles += `
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        max-width: 90vw;
      `;
    } else if (target && target !== document.body) {
      const rect = target.getBoundingClientRect();
      const tooltipSize = { width: 320, height: 200 };

      let top = rect.top;
      let left = rect.left;

      switch (step.position) {
        case 'top':
          top = rect.top - tooltipSize.height - 16;
          left = rect.left + rect.width / 2 - tooltipSize.width / 2;
          break;
        case 'bottom':
          top = rect.bottom + 16;
          left = rect.left + rect.width / 2 - tooltipSize.width / 2;
          break;
        case 'left':
          top = rect.top + rect.height / 2 - tooltipSize.height / 2;
          left = rect.left - tooltipSize.width - 16;
          break;
        case 'right':
          top = rect.top + rect.height / 2 - tooltipSize.height / 2;
          left = rect.right + 16;
          break;
      }

      // Keep tooltip in viewport
      top = Math.max(16, Math.min(top, window.innerHeight - tooltipSize.height - 16));
      left = Math.max(16, Math.min(left, window.innerWidth - tooltipSize.width - 16));

      tooltipStyles += `top: ${top}px; left: ${left}px;`;
    }

    tooltip.style.cssText = tooltipStyles;

    tooltip.innerHTML = `
      <div class="onboarding-header">
        <h3 style="margin: 0 0 var(--spacing-2) 0; font-size: var(--font-size-lg); font-weight: var(--font-weight-semibold);">
          ${step.title}
        </h3>
        <div style="font-size: var(--font-size-xs); color: var(--color-text-secondary); margin-bottom: var(--spacing-4);">
          Step ${(state.currentTour?.currentStepIndex || 0) + 1} of ${state.currentTour?.steps.length || 1}
        </div>
      </div>

      <p style="margin: 0 0 var(--spacing-6) 0; line-height: var(--line-height-relaxed); color: var(--color-text-secondary);">
        ${step.description}
      </p>

      <div class="onboarding-progress" style="margin-bottom: var(--spacing-4);">
        <div style="width: 100%; height: 4px; background: var(--color-gray-200); border-radius: var(--radius-full); overflow: hidden;">
          <div style="width: ${tourProgress.value}%; height: 100%; background: var(--color-primary-500); transition: width 0.3s ease;"></div>
        </div>
      </div>

      <div class="onboarding-actions" style="display: flex; gap: var(--spacing-3); justify-content: space-between;">
        <div>
          ${step.skippable ? `<button class="skip-btn" style="background: none; border: none; color: var(--color-text-tertiary); font-size: var(--font-size-sm); cursor: pointer; padding: var(--spacing-1);">Skip Tour</button>` : ''}
        </div>
        <div style="display: flex; gap: var(--spacing-2);">
          ${!isFirstStep.value ? `<button class="prev-btn btn-base" style="padding: var(--spacing-2) var(--spacing-4); background: var(--color-gray-100); color: var(--color-text-primary); border: 1px solid var(--color-border-primary); border-radius: var(--radius-lg); cursor: pointer;">Previous</button>` : ''}
          <button class="next-btn btn-base" style="padding: var(--spacing-2) var(--spacing-4); background: var(--color-primary-500); color: white; border: none; border-radius: var(--radius-lg); cursor: pointer;">
            ${isLastStep.value ? 'Finish' : 'Next'}
          </button>
        </div>
      </div>
    `;

    document.body.appendChild(tooltip);
    tooltipElement.value = tooltip;

    // Add event listeners
    const skipBtn = tooltip.querySelector('.skip-btn');
    const prevBtn = tooltip.querySelector('.prev-btn');
    const nextBtn = tooltip.querySelector('.next-btn');

    if (skipBtn) {
      skipBtn.addEventListener('click', skipTour);
    }

    if (prevBtn) {
      prevBtn.addEventListener('click', previousStep);
    }

    if (nextBtn) {
      nextBtn.addEventListener('click', () => {
        if (step.action) {
          performAction(step.action);
        }
        nextStep();
      });
    }

    // Add keyboard navigation
    const handleKeyDown = (e: KeyboardEvent) => {
      switch (e.key) {
        case 'Escape':
          skipTour();
          break;
        case 'ArrowRight':
        case 'Enter':
          if (step.action) {
            performAction(step.action);
          }
          nextStep();
          break;
        case 'ArrowLeft':
          if (!isFirstStep.value) {
            previousStep();
          }
          break;
      }
    };

    tooltip.addEventListener('keydown', handleKeyDown);
    tooltip.setAttribute('tabindex', '0');
    tooltip.focus();

    // Animation
    if (state.enableAnimations) {
      tooltip.style.opacity = '0';
      tooltip.style.transform += ' scale(0.9)';

      setTimeout(() => {
        tooltip.style.transition = 'opacity 0.3s ease, transform 0.3s ease';
        tooltip.style.opacity = '1';
        tooltip.style.transform = tooltip.style.transform.replace(' scale(0.9)', '');
      }, 50);
    }
  };

  // Perform step action
  const performAction = (action: OnboardingStep['action']): void => {
    if (!action) return;

    const element = action.element ? document.querySelector(action.element) as HTMLElement : null;

    switch (action.type) {
      case 'click':
        if (element) {
          element.click();
        }
        break;
      case 'hover':
        if (element) {
          element.dispatchEvent(new MouseEvent('mouseenter', { bubbles: true }));
        }
        break;
      case 'input':
        if (element && action.value) {
          (element as HTMLInputElement).value = action.value;
          element.dispatchEvent(new Event('input', { bubbles: true }));
        }
        break;
      case 'scroll':
        if (element) {
          element.scrollIntoView({ behavior: 'smooth', block: 'center' });
        }
        break;
    }
  };

  // Announce step to screen readers
  const announceStep = (step: OnboardingStep): void => {
    const announcement = `Onboarding step ${(state.currentTour?.currentStepIndex || 0) + 1}: ${step.title}. ${step.description}`;

    // Create live region for screen readers
    const announcer = document.createElement('div');
    announcer.setAttribute('aria-live', 'polite');
    announcer.setAttribute('aria-atomic', 'true');
    announcer.className = 'sr-only';
    announcer.textContent = announcement;

    document.body.appendChild(announcer);

    setTimeout(() => {
      document.body.removeChild(announcer);
    }, 1000);
  };

  // Navigation methods
  const nextStep = (): void => {
    if (!state.currentTour) return;

    // Mark current step as completed
    if (currentStep.value) {
      currentStep.value.completed = true;
    }

    if (isLastStep.value) {
      completeTour();
    } else {
      state.currentTour.currentStepIndex++;
      showCurrentStep();
    }
  };

  const previousStep = (): void => {
    if (!state.currentTour || isFirstStep.value) return;

    state.currentTour.currentStepIndex--;
    showCurrentStep();
  };

  const skipTour = (): void => {
    if (!state.currentTour) return;

    console.log(`🎯 Skipping tour: ${state.currentTour.name}`);
    endTour();
  };

  const completeTour = (): void => {
    if (!state.currentTour) return;

    const tour = state.currentTour;
    tour.completedAt = new Date();

    // Mark as completed
    if (!state.completedTours.includes(tour.id)) {
      state.completedTours.push(tour.id);
    }

    // Save progress
    state.userProgress[tour.id] = {
      completedAt: tour.completedAt,
      version: tour.version,
      stepsCompleted: tour.steps.filter(s => s.completed).length
    };

    console.log(`✅ Tour completed: ${tour.name}`);

    // Show completion message
    showCompletionMessage(tour);

    endTour();
  };

  const endTour = (): void => {
    removeOverlay();
    removeTooltip();

    state.isActive = false;
    state.currentTour = null;
  };

  // Show completion message
  const showCompletionMessage = (tour: OnboardingTour): void => {
    const message = document.createElement('div');
    message.style.cssText = `
      position: fixed;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      z-index: 10001;
      background: var(--color-surface-primary);
      border: 1px solid var(--color-border-success);
      border-radius: var(--radius-xl);
      box-shadow: var(--shadow-xl);
      padding: var(--spacing-8);
      text-align: center;
      max-width: 400px;
    `;

    message.innerHTML = `
      <div style="font-size: 3rem; margin-bottom: var(--spacing-4);">🎉</div>
      <h3 style="margin: 0 0 var(--spacing-2) 0; color: var(--color-success-600);">Tour Complete!</h3>
      <p style="margin: 0 0 var(--spacing-4) 0; color: var(--color-text-secondary);">
        You've successfully completed the ${tour.name}. You're now ready to use all the new features!
      </p>
      <button class="close-btn btn-base" style="background: var(--color-success-500); color: white; border: none; padding: var(--spacing-3) var(--spacing-6); border-radius: var(--radius-lg); cursor: pointer;">
        Get Started
      </button>
    `;

    document.body.appendChild(message);

    const closeBtn = message.querySelector('.close-btn');
    if (closeBtn) {
      closeBtn.addEventListener('click', () => {
        document.body.removeChild(message);
      });
    }

    // Auto-remove after 5 seconds
    setTimeout(() => {
      if (document.body.contains(message)) {
        document.body.removeChild(message);
      }
    }, 5000);
  };

  // Utility methods
  const removeOverlay = (): void => {
    if (overlayElement.value) {
      document.body.removeChild(overlayElement.value);
      overlayElement.value = null;
    }
  };

  const removeTooltip = (): void => {
    if (tooltipElement.value) {
      document.body.removeChild(tooltipElement.value);
      tooltipElement.value = null;
    }
  };

  // Auto-start tours for new users
  const checkForAutoStart = (): void => {
    // Check if user has completed the main tour
    const mainTourCompleted = state.completedTours.includes('real-time-features');
    const isNewUser = state.completedTours.length === 0;

    if (isNewUser || !mainTourCompleted) {
      // Start the main tour after a brief delay
      setTimeout(() => {
        startTour('real-time-features');
      }, 2000);
    }
  };

  // Reset tours (for development/testing)
  const resetTours = (): void => {
    state.completedTours = [];
    state.userProgress = {};
    console.log('🔄 Tours reset');
  };

  // Initialize
  onMounted(() => {
    initializeDefaultTours();

    // Load saved progress from localStorage
    const savedProgress = localStorage.getItem('onboarding-progress');
    if (savedProgress) {
      try {
        const progress = JSON.parse(savedProgress);
        state.completedTours = progress.completedTours || [];
        state.userProgress = progress.userProgress || {};
      } catch (error) {
        console.warn('Failed to load onboarding progress:', error);
      }
    }

    // Check for auto-start
    nextTick(() => {
      checkForAutoStart();
    });
  });

  // Save progress when it changes
  watch(() => [state.completedTours, state.userProgress], () => {
    const progress = {
      completedTours: state.completedTours,
      userProgress: state.userProgress
    };
    localStorage.setItem('onboarding-progress', JSON.stringify(progress));
  }, { deep: true });

  return {
    // State
    state: readonly(state),
    currentStep,
    tourProgress,
    availableToursForUser,

    // Computed
    isFirstStep,
    isLastStep,

    // Methods
    startTour,
    nextStep,
    previousStep,
    skipTour,
    completeTour,
    endTour,
    resetTours,

    // Utilities
    checkForAutoStart
  };
};

export default useOnboarding;

import { ref, reactive, computed, watch, onMounted, nextTick, readonly } from 'vue';

// TypeScript interfaces
export interface OnboardingStep {
  id: string;
  title: string;
  description: string;
  target: string; // CSS selector
  position: 'top' | 'bottom' | 'left' | 'right' | 'center';
  action?: {
    type: 'click' | 'hover' | 'input' | 'scroll';
    element?: string;
    value?: string;
  };
  condition?: () => boolean;
  completed: boolean;
  skippable: boolean;
  highlight: boolean;
}

export interface OnboardingTour {
  id: string;
  name: string;
  description: string;
  steps: OnboardingStep[];
  isActive: boolean;
  currentStepIndex: number;
  completedAt?: Date;
  version: string;
}

export interface OnboardingState {
  isActive: boolean;
  currentTour: OnboardingTour | null;
  availableTours: OnboardingTour[];
  completedTours: string[];
  userProgress: Record<string, any>;
  showTooltips: boolean;
  enableAnimations: boolean;
}

export const useOnboarding = () => {
  // Reactive state
  const state = reactive<OnboardingState>({
    isActive: false,
    currentTour: null,
    availableTours: [],
    completedTours: [],
    userProgress: {},
    showTooltips: true,
    enableAnimations: true
  });

  const overlayElement = ref<HTMLElement | null>(null);
  const tooltipElement = ref<HTMLElement | null>(null);

  // Tour definitions
  const defaultTours: OnboardingTour[] = [
    {
      id: 'real-time-features',
      name: 'Real-time Features Tour',
      description: 'Discover the new real-time capabilities and AI-powered features',
      version: '1.0.0',
      isActive: false,
      currentStepIndex: 0,
      steps: [
        {
          id: 'notifications',
          title: 'Smart Notifications',
          description: 'Your notifications now use AI to prioritize important alerts and group related messages.',
          target: '.notifications-container',
          position: 'left',
          completed: false,
          skippable: true,
          highlight: true
        },
        {
          id: 'connection-status',
          title: 'Real-time Connection',
          description: 'See your connection status and get updates instantly when your data changes.',
          target: '.connection-indicator',
          position: 'bottom',
          completed: false,
          skippable: true,
          highlight: true
        },
        {
          id: 'ai-chat',
          title: 'AI Assistant',
          description: 'Chat with your AI assistant for content creation, analytics insights, and workflow optimization.',
          target: '[title="AI Assistant"]',
          position: 'right',
          action: {
            type: 'click',
            element: '[title="AI Assistant"]'
          },
          completed: false,
          skippable: true,
          highlight: true
        },
        {
          id: 'accessibility',
          title: 'Accessibility Features',
          description: 'Press Ctrl+K for chat, Ctrl+N for notifications, and Ctrl+/ for search. Full keyboard navigation is available.',
          target: 'body',
          position: 'center',
          completed: false,
          skippable: true,
          highlight: false
        },
        {
          id: 'mobile-optimization',
          title: 'Mobile Experience',
          description: 'Enjoy optimized touch gestures, PWA capabilities, and battery-conscious performance on mobile devices.',
          target: 'body',
          position: 'center',
          condition: () => window.innerWidth <= 768,
          completed: false,
          skippable: true,
          highlight: false
        }
      ]
    },
    {
      id: 'advanced-ai',
      name: 'Advanced AI Features',
      description: 'Learn about the enhanced AI capabilities for content creation and analytics',
      version: '1.0.0',
      isActive: false,
      currentStepIndex: 0,
      steps: [
        {
          id: 'context-analysis',
          title: 'Context-Aware Suggestions',
          description: 'The AI now analyzes your current dashboard context to provide relevant suggestions.',
          target: '.chat-widget-container',
          position: 'left',
          completed: false,
          skippable: true,
          highlight: true
        },
        {
          id: 'predictive-text',
          title: 'Predictive Text',
          description: 'Get intelligent text completion suggestions while creating posts.',
          target: '.create-post-form',
          position: 'top',
          condition: () => window.location.pathname.includes('/posts/create'),
          completed: false,
          skippable: true,
          highlight: true
        },
        {
          id: 'smart-scheduling',
          title: 'Smart Scheduling',
          description: 'AI analyzes your audience data to recommend optimal posting times.',
          target: '.scheduling-section',
          position: 'right',
          condition: () => window.location.pathname.includes('/posts/create'),
          completed: false,
          skippable: true,
          highlight: true
        },
        {
          id: 'analytics-insights',
          title: 'Natural Language Analytics',
          description: 'Ask questions about your analytics in plain English and get detailed insights.',
          target: '.analytics-dashboard',
          position: 'top',
          condition: () => window.location.pathname.includes('/analytics'),
          completed: false,
          skippable: true,
          highlight: true
        }
      ]
    }
  ];

  // Computed properties
  const currentStep = computed(() => {
    if (!state.currentTour) return null;
    return state.currentTour.steps[state.currentTour.currentStepIndex] || null;
  });

  const isLastStep = computed(() => {
    if (!state.currentTour) return false;
    return state.currentTour.currentStepIndex >= state.currentTour.steps.length - 1;
  });

  const isFirstStep = computed(() => {
    if (!state.currentTour) return false;
    return state.currentTour.currentStepIndex === 0;
  });

  const tourProgress = computed(() => {
    if (!state.currentTour) return 0;
    return Math.round((state.currentTour.currentStepIndex / state.currentTour.steps.length) * 100);
  });

  const availableToursForUser = computed(() => {
    return state.availableTours.filter(tour => {
      // Don't show completed tours unless they have new versions
      const wasCompleted = state.completedTours.includes(tour.id);
      if (wasCompleted) {
        // Check if there's a newer version
        const completedVersion = state.userProgress[tour.id]?.version;
        return tour.version !== completedVersion;
      }
      return true;
    });
  });

  // Initialize with default tours
  const initializeDefaultTours = (): void => {
    state.availableTours = [...defaultTours];
    console.log('🎯 Onboarding tours initialized:', state.availableTours.length);
  };

  // Start a tour
  const startTour = (tourId: string): void => {
    const tour = state.availableTours.find(t => t.id === tourId);
    if (!tour) {
      console.warn(`Tour ${tourId} not found`);
      return;
    }

    state.currentTour = { ...tour };
    state.currentTour.currentStepIndex = 0;
    state.currentTour.isActive = true;
    state.isActive = true;

    console.log(`🎯 Starting tour: ${tour.name}`);

    nextTick(() => {
      showCurrentStep();
    });
  };

  // Show current step
  const showCurrentStep = (): void => {
    if (!currentStep.value) return;

    const step = currentStep.value;

    // Check condition if present
    if (step.condition && !step.condition()) {
      console.log(`Skipping step ${step.id} - condition not met`);
      nextStep();
      return;
    }

    // Find target element
    const targetElement = document.querySelector(step.target) as HTMLElement;
    if (!targetElement && step.target !== 'body') {
      console.warn(`Target element not found: ${step.target}`);
      nextStep();
      return;
    }

    // Create overlay and highlight
    createOverlay(targetElement, step);
    createTooltip(targetElement, step);

    // Announce to screen readers
    announceStep(step);
  };

  // Create overlay with highlight
  const createOverlay = (target: HTMLElement | null, step: OnboardingStep): void => {
    removeOverlay();

    if (!step.highlight) return;

    const overlay = document.createElement('div');
    overlay.className = 'onboarding-overlay';
    overlay.style.cssText = `
      position: fixed;
      top: 0;
      left: 0;
      width: 100vw;
      height: 100vh;
      background: rgba(0, 0, 0, 0.6);
      z-index: 9999;
      pointer-events: none;
      transition: opacity 0.3s ease;
    `;

    if (target && target !== document.body) {
      const rect = target.getBoundingClientRect();
      const highlightPadding = 8;

      // Create highlight cutout
      const cutout = document.createElement('div');
      cutout.style.cssText = `
        position: absolute;
        top: ${rect.top - highlightPadding}px;
        left: ${rect.left - highlightPadding}px;
        width: ${rect.width + highlightPadding * 2}px;
        height: ${rect.height + highlightPadding * 2}px;
        box-shadow: 0 0 0 9999px rgba(0, 0, 0, 0.6);
        border-radius: var(--radius-lg);
        pointer-events: auto;
      `;

      overlay.appendChild(cutout);
    }

    document.body.appendChild(overlay);
    overlayElement.value = overlay;

    // Add click handler to close
    overlay.addEventListener('click', (e) => {
      if (e.target === overlay) {
        skipTour();
      }
    });
  };

  // Create tooltip
  const createTooltip = (target: HTMLElement | null, step: OnboardingStep): void => {
    removeTooltip();

    const tooltip = document.createElement('div');
    tooltip.className = 'onboarding-tooltip';

    const isCenter = step.position === 'center';
    const isMobile = window.innerWidth <= 768;

    let tooltipStyles = `
      position: fixed;
      z-index: 10000;
      background: var(--color-surface-primary);
      border: 1px solid var(--color-border-primary);
      border-radius: var(--radius-xl);
      box-shadow: var(--shadow-xl);
      padding: var(--spacing-6);
      max-width: 320px;
      font-family: var(--font-family-sans);
      color: var(--color-text-primary);
    `;

    if (isCenter || isMobile) {
      tooltipStyles += `
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        max-width: 90vw;
      `;
    } else if (target && target !== document.body) {
      const rect = target.getBoundingClientRect();
      const tooltipSize = { width: 320, height: 200 };

      let top = rect.top;
      let left = rect.left;

      switch (step.position) {
        case 'top':
          top = rect.top - tooltipSize.height - 16;
          left = rect.left + rect.width / 2 - tooltipSize.width / 2;
          break;
        case 'bottom':
          top = rect.bottom + 16;
          left = rect.left + rect.width / 2 - tooltipSize.width / 2;
          break;
        case 'left':
          top = rect.top + rect.height / 2 - tooltipSize.height / 2;
          left = rect.left - tooltipSize.width - 16;
          break;
        case 'right':
          top = rect.top + rect.height / 2 - tooltipSize.height / 2;
          left = rect.right + 16;
          break;
      }

      // Keep tooltip in viewport
      top = Math.max(16, Math.min(top, window.innerHeight - tooltipSize.height - 16));
      left = Math.max(16, Math.min(left, window.innerWidth - tooltipSize.width - 16));

      tooltipStyles += `top: ${top}px; left: ${left}px;`;
    }

    tooltip.style.cssText = tooltipStyles;

    tooltip.innerHTML = `
      <div class="onboarding-header">
        <h3 style="margin: 0 0 var(--spacing-2) 0; font-size: var(--font-size-lg); font-weight: var(--font-weight-semibold);">
          ${step.title}
        </h3>
        <div style="font-size: var(--font-size-xs); color: var(--color-text-secondary); margin-bottom: var(--spacing-4);">
          Step ${(state.currentTour?.currentStepIndex || 0) + 1} of ${state.currentTour?.steps.length || 1}
        </div>
      </div>

      <p style="margin: 0 0 var(--spacing-6) 0; line-height: var(--line-height-relaxed); color: var(--color-text-secondary);">
        ${step.description}
      </p>

      <div class="onboarding-progress" style="margin-bottom: var(--spacing-4);">
        <div style="width: 100%; height: 4px; background: var(--color-gray-200); border-radius: var(--radius-full); overflow: hidden;">
          <div style="width: ${tourProgress.value}%; height: 100%; background: var(--color-primary-500); transition: width 0.3s ease;"></div>
        </div>
      </div>

      <div class="onboarding-actions" style="display: flex; gap: var(--spacing-3); justify-content: space-between;">
        <div>
          ${step.skippable ? `<button class="skip-btn" style="background: none; border: none; color: var(--color-text-tertiary); font-size: var(--font-size-sm); cursor: pointer; padding: var(--spacing-1);">Skip Tour</button>` : ''}
        </div>
        <div style="display: flex; gap: var(--spacing-2);">
          ${!isFirstStep.value ? `<button class="prev-btn inline-flex items-center justify-center gap-2 px-4 py-2 rounded-lg font-medium bg-gray-100 text-gray-900 dark:text-gray-100 border border-gray-200 dark:border-gray-700 hover:bg-gray-200 dark:hover:bg-gray-700 focus-ring transition-fast cursor-pointer">Previous</button>` : ''}
          <button class="next-btn inline-flex items-center justify-center gap-2 px-4 py-2 rounded-lg font-medium bg-primary-500 text-white hover:bg-primary-600 focus-ring transition-fast cursor-pointer">
            ${isLastStep.value ? 'Finish' : 'Next'}
          </button>
        </div>
      </div>
    `;

    document.body.appendChild(tooltip);
    tooltipElement.value = tooltip;

    // Add event listeners
    const skipBtn = tooltip.querySelector('.skip-btn');
    const prevBtn = tooltip.querySelector('.prev-btn');
    const nextBtn = tooltip.querySelector('.next-btn');

    if (skipBtn) {
      skipBtn.addEventListener('click', skipTour);
    }

    if (prevBtn) {
      prevBtn.addEventListener('click', previousStep);
    }

    if (nextBtn) {
      nextBtn.addEventListener('click', () => {
        if (step.action) {
          performAction(step.action);
        }
        nextStep();
      });
    }

    // Add keyboard navigation
    const handleKeyDown = (e: KeyboardEvent) => {
      switch (e.key) {
        case 'Escape':
          skipTour();
          break;
        case 'ArrowRight':
        case 'Enter':
          if (step.action) {
            performAction(step.action);
          }
          nextStep();
          break;
        case 'ArrowLeft':
          if (!isFirstStep.value) {
            previousStep();
          }
          break;
      }
    };

    tooltip.addEventListener('keydown', handleKeyDown);
    tooltip.setAttribute('tabindex', '0');
    tooltip.focus();

    // Animation
    if (state.enableAnimations) {
      tooltip.style.opacity = '0';
      tooltip.style.transform += ' scale(0.9)';

      setTimeout(() => {
        tooltip.style.transition = 'opacity 0.3s ease, transform 0.3s ease';
        tooltip.style.opacity = '1';
        tooltip.style.transform = tooltip.style.transform.replace(' scale(0.9)', '');
      }, 50);
    }
  };

  // Perform step action
  const performAction = (action: OnboardingStep['action']): void => {
    if (!action) return;

    const element = action.element ? document.querySelector(action.element) as HTMLElement : null;

    switch (action.type) {
      case 'click':
        if (element) {
          element.click();
        }
        break;
      case 'hover':
        if (element) {
          element.dispatchEvent(new MouseEvent('mouseenter', { bubbles: true }));
        }
        break;
      case 'input':
        if (element && action.value) {
          (element as HTMLInputElement).value = action.value;
          element.dispatchEvent(new Event('input', { bubbles: true }));
        }
        break;
      case 'scroll':
        if (element) {
          element.scrollIntoView({ behavior: 'smooth', block: 'center' });
        }
        break;
    }
  };

  // Announce step to screen readers
  const announceStep = (step: OnboardingStep): void => {
    const announcement = `Onboarding step ${(state.currentTour?.currentStepIndex || 0) + 1}: ${step.title}. ${step.description}`;

    // Create live region for screen readers
    const announcer = document.createElement('div');
    announcer.setAttribute('aria-live', 'polite');
    announcer.setAttribute('aria-atomic', 'true');
    announcer.className = 'sr-only';
    announcer.textContent = announcement;

    document.body.appendChild(announcer);

    setTimeout(() => {
      document.body.removeChild(announcer);
    }, 1000);
  };

  // Navigation methods
  const nextStep = (): void => {
    if (!state.currentTour) return;

    // Mark current step as completed
    if (currentStep.value) {
      currentStep.value.completed = true;
    }

    if (isLastStep.value) {
      completeTour();
    } else {
      state.currentTour.currentStepIndex++;
      showCurrentStep();
    }
  };

  const previousStep = (): void => {
    if (!state.currentTour || isFirstStep.value) return;

    state.currentTour.currentStepIndex--;
    showCurrentStep();
  };

  const skipTour = (): void => {
    if (!state.currentTour) return;

    console.log(`🎯 Skipping tour: ${state.currentTour.name}`);
    endTour();
  };

  const completeTour = (): void => {
    if (!state.currentTour) return;

    const tour = state.currentTour;
    tour.completedAt = new Date();

    // Mark as completed
    if (!state.completedTours.includes(tour.id)) {
      state.completedTours.push(tour.id);
    }

    // Save progress
    state.userProgress[tour.id] = {
      completedAt: tour.completedAt,
      version: tour.version,
      stepsCompleted: tour.steps.filter(s => s.completed).length
    };

    console.log(`✅ Tour completed: ${tour.name}`);

    // Show completion message
    showCompletionMessage(tour);

    endTour();
  };

  const endTour = (): void => {
    removeOverlay();
    removeTooltip();

    state.isActive = false;
    state.currentTour = null;
  };

  // Show completion message
  const showCompletionMessage = (tour: OnboardingTour): void => {
    const message = document.createElement('div');
    message.style.cssText = `
      position: fixed;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      z-index: 10001;
      background: var(--color-surface-primary);
      border: 1px solid var(--color-border-success);
      border-radius: var(--radius-xl);
      box-shadow: var(--shadow-xl);
      padding: var(--spacing-8);
      text-align: center;
      max-width: 400px;
    `;

    message.innerHTML = `
      <div style="font-size: 3rem; margin-bottom: var(--spacing-4);">🎉</div>
      <h3 style="margin: 0 0 var(--spacing-2) 0; color: var(--color-success-600);">Tour Complete!</h3>
      <p style="margin: 0 0 var(--spacing-4) 0; color: var(--color-text-secondary);">
        You've successfully completed the ${tour.name}. You're now ready to use all the new features!
      </p>
      <button class="close-btn btn-base" style="background: var(--color-success-500); color: white; border: none; padding: var(--spacing-3) var(--spacing-6); border-radius: var(--radius-lg); cursor: pointer;">
        Get Started
      </button>
    `;

    document.body.appendChild(message);

    const closeBtn = message.querySelector('.close-btn');
    if (closeBtn) {
      closeBtn.addEventListener('click', () => {
        document.body.removeChild(message);
      });
    }

    // Auto-remove after 5 seconds
    setTimeout(() => {
      if (document.body.contains(message)) {
        document.body.removeChild(message);
      }
    }, 5000);
  };

  // Utility methods
  const removeOverlay = (): void => {
    if (overlayElement.value) {
      document.body.removeChild(overlayElement.value);
      overlayElement.value = null;
    }
  };

  const removeTooltip = (): void => {
    if (tooltipElement.value) {
      document.body.removeChild(tooltipElement.value);
      tooltipElement.value = null;
    }
  };

  // Auto-start tours for new users
  const checkForAutoStart = (): void => {
    // Check if user has completed the main tour
    const mainTourCompleted = state.completedTours.includes('real-time-features');
    const isNewUser = state.completedTours.length === 0;

    if (isNewUser || !mainTourCompleted) {
      // Start the main tour after a brief delay
      setTimeout(() => {
        startTour('real-time-features');
      }, 2000);
    }
  };

  // Reset tours (for development/testing)
  const resetTours = (): void => {
    state.completedTours = [];
    state.userProgress = {};
    console.log('🔄 Tours reset');
  };

  // Initialize
  onMounted(() => {
    initializeDefaultTours();

    // Load saved progress from localStorage
    const savedProgress = localStorage.getItem('onboarding-progress');
    if (savedProgress) {
      try {
        const progress = JSON.parse(savedProgress);
        state.completedTours = progress.completedTours || [];
        state.userProgress = progress.userProgress || {};
      } catch (error) {
        console.warn('Failed to load onboarding progress:', error);
      }
    }

    // Check for auto-start
    nextTick(() => {
      checkForAutoStart();
    });
  });

  // Save progress when it changes
  watch(() => [state.completedTours, state.userProgress], () => {
    const progress = {
      completedTours: state.completedTours,
      userProgress: state.userProgress
    };
    localStorage.setItem('onboarding-progress', JSON.stringify(progress));
  }, { deep: true });

  return {
    // State
    state: readonly(state),
    currentStep,
    tourProgress,
    availableToursForUser,

    // Computed
    isFirstStep,
    isLastStep,

    // Methods
    startTour,
    nextStep,
    previousStep,
    skipTour,
    completeTour,
    endTour,
    resetTours,

    // Utilities
    checkForAutoStart
  };
};

export default useOnboarding;

