@import "tailwindcss";

/* Define cascade layer order - explicit precedence */
@layer reset, theme, composition, utilities, components, overrides;

/* Import design system files into appropriate layers */
@import "./design-system.css" layer(theme);
@import "./typography.css" layer(components);
@import "./components.css" layer(components);
@import "./utilities.css" layer(utilities);

/*darkMode: ["class", '[data-theme="dark"]'],*/
@custom-variant dark (&:where([data-theme=dark], [data-theme=dark] *));

/* Accessibility-focused custom variants */
@custom-variant motion-safe {
  @media (prefers-reduced-motion: no-preference) {
    @slot;
  }
}

@custom-variant motion-reduce {
  @media (prefers-reduced-motion: reduce) {
    @slot;
  }
}

@custom-variant contrast-high {
  @media (prefers-contrast: high) {
    @slot;
  }
}

@custom-variant touch {
  @media (pointer: coarse) {
    @slot;
  }
}

@custom-variant mouse {
  @media (pointer: fine) {
    @slot;
  }
}

@custom-variant hover-supported {
  @media (hover: hover) {
    @slot;
  }
}

@theme {
  /* Use design system tokens for Tailwind */
  --color-primary-50: var(--color-primary-50);
  --color-primary-100: var(--color-primary-100);
  --color-primary-200: var(--color-primary-200);
  --color-primary-300: var(--color-primary-300);
  --color-primary-400: var(--color-primary-400);
  --color-primary-500: var(--color-primary-500);
  --color-primary-600: var(--color-primary-600);
  --color-primary-700: var(--color-primary-700);
  --color-primary-800: var(--color-primary-800);
  --color-primary-900: var(--color-primary-900);
  --color-primary-950: var(--color-primary-950);

  /* Success colors */
  --color-success-50: var(--color-success-50);
  --color-success-100: var(--color-success-100);
  --color-success-200: var(--color-success-200);
  --color-success-300: var(--color-success-300);
  --color-success-400: var(--color-success-400);
  --color-success-500: var(--color-success-500);
  --color-success-600: var(--color-success-600);
  --color-success-700: var(--color-success-700);
  --color-success-800: var(--color-success-800);
  --color-success-900: var(--color-success-900);
  --color-success-950: var(--color-success-950);

  /* Warning colors */
  --color-warning-50: var(--color-warning-50);
  --color-warning-100: var(--color-warning-100);
  --color-warning-200: var(--color-warning-200);
  --color-warning-300: var(--color-warning-300);
  --color-warning-400: var(--color-warning-400);
  --color-warning-500: var(--color-warning-500);
  --color-warning-600: var(--color-warning-600);
  --color-warning-700: var(--color-warning-700);
  --color-warning-800: var(--color-warning-800);
  --color-warning-900: var(--color-warning-900);
  --color-warning-950: var(--color-warning-950);

  /* Error colors */
  --color-error-50: var(--color-error-50);
  --color-error-100: var(--color-error-100);
  --color-error-200: var(--color-error-200);
  --color-error-300: var(--color-error-300);
  --color-error-400: var(--color-error-400);
  --color-error-500: var(--color-error-500);
  --color-error-600: var(--color-error-600);
  --color-error-700: var(--color-error-700);
  --color-error-800: var(--color-error-800);
  --color-error-900: var(--color-error-900);
  --color-error-950: var(--color-error-950);

  /* Info colors */
  --color-info-50: var(--color-info-50);
  --color-info-100: var(--color-info-100);
  --color-info-200: var(--color-info-200);
  --color-info-300: var(--color-info-300);
  --color-info-400: var(--color-info-400);
  --color-info-500: var(--color-info-500);
  --color-info-600: var(--color-info-600);
  --color-info-700: var(--color-info-700);
  --color-info-800: var(--color-info-800);
  --color-info-900: var(--color-info-900);
  --color-info-950: var(--color-info-950);

  /* Font families */
  --font-sans: var(--font-family-sans);
  --font-mono: var(--font-family-mono);
  --font-emoji: var(--font-family-emoji);

  /* Design system spacing */
  --spacing-0: var(--spacing-0);
  --spacing-px: var(--spacing-px);
  --spacing-0_5: var(--spacing-0_5);
  --spacing-1: var(--spacing-1);
  --spacing-1_5: var(--spacing-1_5);
  --spacing-2: var(--spacing-2);
  --spacing-2_5: var(--spacing-2_5);
  --spacing-3: var(--spacing-3);
  --spacing-3_5: var(--spacing-3_5);
  --spacing-4: var(--spacing-4);
  --spacing-5: var(--spacing-5);
  --spacing-6: var(--spacing-6);
  --spacing-7: var(--spacing-7);
  --spacing-8: var(--spacing-8);
  --spacing-9: var(--spacing-9);
  --spacing-10: var(--spacing-10);
  --spacing-11: var(--spacing-11);
  --spacing-12: var(--spacing-12);
  --spacing-14: var(--spacing-14);
  --spacing-16: var(--spacing-16);
  --spacing-20: var(--spacing-20);
  --spacing-24: var(--spacing-24);
  --spacing-28: var(--spacing-28);
  --spacing-32: var(--spacing-32);

  /* Border radius */
  --radius-none: var(--radius-none);
  --radius-sm: var(--radius-sm);
  --radius-base: var(--radius-base);
  --radius-md: var(--radius-md);
  --radius-lg: var(--radius-lg);
  --radius-xl: var(--radius-xl);
  --radius-2xl: var(--radius-2xl);
  --radius-3xl: var(--radius-3xl);
  --radius-full: var(--radius-full);

  /* Animation Durations - synced with constants/animations.ts */
  --transition-duration-fast: 200ms;      /* ANIMATION_DURATION.FAST */
  --transition-duration-base: 200ms;      /* Same as fast for consistency */
  --transition-duration-medium: 300ms;    /* ANIMATION_DURATION.NORMAL */
  --transition-duration-slow: 500ms;      /* ANIMATION_DURATION.SLOW */
  --transition-duration-celebration: 2000ms; /* ANIMATION_DURATION.CELEBRATION */

  /* Animation Easing Functions */
  --ease-linear: linear;
  --ease-in: cubic-bezier(0.4, 0, 1, 1);
  --ease-out: cubic-bezier(0, 0, 0.2, 1);
  --ease-in-out: cubic-bezier(0.4, 0, 0.2, 1);
  --ease-bounce: cubic-bezier(0.68, -0.55, 0.265, 1.55);
  --ease-smooth: cubic-bezier(0.16, 1, 0.3, 1);
  --ease-sharp: cubic-bezier(0.4, 0, 0.2, 1);
  --ease-elastic: cubic-bezier(0.68, -0.55, 0.265, 1.55);

  /* Animation Keyframes */
  @keyframes fade-in {
    from { opacity: 0; }
    to { opacity: 1; }
  }

  @keyframes fade-out {
    from { opacity: 1; }
    to { opacity: 0; }
  }

  @keyframes slide-in-up {
    from { transform: translateY(1rem); opacity: 0; }
    to { transform: translateY(0); opacity: 1; }
  }

  @keyframes slide-in-down {
    from { transform: translateY(-1rem); opacity: 0; }
    to { transform: translateY(0); opacity: 1; }
  }

  @keyframes scale-in {
    from { transform: scale(0.9); opacity: 0; }
    to { transform: scale(1); opacity: 1; }
  }

  @keyframes scale-out {
    from { transform: scale(1); opacity: 1; }
    to { transform: scale(0.9); opacity: 0; }
  }

  /* Predefined Animations */
  --animate-fade-in: fade-in var(--transition-duration-base) var(--ease-out);
  --animate-fade-out: fade-out var(--transition-duration-base) var(--ease-in);
  --animate-slide-in-up: slide-in-up var(--transition-duration-medium) var(--ease-out);
  --animate-slide-in-down: slide-in-down var(--transition-duration-medium) var(--ease-out);
  --animate-scale-in: scale-in var(--transition-duration-base) var(--ease-out);
  --animate-scale-out: scale-out var(--transition-duration-base) var(--ease-in);
  --animate-pulse: pulse 2s var(--ease-in-out) infinite;
  --animate-bounce: bounce 1s infinite;
  --animate-spin: spin 1s linear infinite;
  --animate-ping: ping 1s var(--ease-in-out) infinite;

  /* Box Shadows (from tailwind.config.mjs) */
  --shadow-xs: 0 1px 2px 0 rgb(0 0 0 / 0.05);
  --shadow-sm: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
  --shadow-DEFAULT: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
  --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
  --shadow-xl: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1);
  --shadow-2xl: 0 25px 50px -12px rgb(0 0 0 / 0.25);
  --shadow-inner: inset 0 2px 4px 0 rgb(0 0 0 / 0.05);
  --shadow-focus-primary: 0 0 0 3px rgb(0 124 137 / 0.1);
  --shadow-focus-error: 0 0 0 3px rgb(239 68 68 / 0.1);
  --shadow-focus-success: 0 0 0 3px rgb(16 185 129 / 0.1);

  /* Z-Index Scale */
  --z-behind: -1;
  --z-0: 0;
  --z-10: 10;
  --z-20: 20;
  --z-30: 30;
  --z-40: 40;
  --z-50: 50;
  --z-dropdown: 1000;
  --z-sticky: 1020;
  --z-fixed: 1030;
  --z-modal-backdrop: 1040;
  --z-modal: 1050;
  --z-popover: 1060;
  --z-tooltip: 1070;
  --z-toast: 1080;
  --z-notification: 1000;
  --z-chat: 900;

  /* Responsive Breakpoints */
  --breakpoint-xs: 375px;
  --breakpoint-sm: 640px;
  --breakpoint-md: 768px;
  --breakpoint-lg: 1024px;
  --breakpoint-xl: 1280px;
  --breakpoint-2xl: 1536px;
  --breakpoint-mobile: 480px;
  --breakpoint-tablet: 768px;
  --breakpoint-desktop: 1440px;
  --breakpoint-ultrawide: 1920px;
}

.label-position {
  top: min(min(-1.5vh, -1.5vw), -1.5rem);
}

/* ===== COMPONENT LAYER: Reusable patterns ===== */
@layer components {
  /* Prose styles for documentation */
  .prose {
    @apply text-gray-900 dark:text-gray-100;
  }

.prose h1,
.prose h2,
.prose h3,
.prose h4,
.prose h5,
.prose h6 {
  @apply text-gray-900 dark:text-white font-semibold;
}

.prose h1 {
  @apply text-4xl sm:text-5xl leading-tight mt-12 mb-6;
}

.prose h2 {
  @apply text-3xl sm:text-4xl leading-snug mt-10 mb-5;
}

.prose h3 {
  @apply text-2xl sm:text-3xl leading-snug mt-8 mb-4;
}

.prose h4 {
  @apply text-xl sm:text-2xl leading-snug mt-6 mb-3;
}

.prose h5 {
  @apply text-lg leading-snug mt-5 mb-2;
}

.prose h6 {
  @apply text-base uppercase tracking-wide text-gray-600 dark:text-gray-400 mt-4 mb-2;
}

.prose a {
  @apply text-primary-600 dark:text-primary-400 hover:text-primary-700 dark:hover:text-primary-300 underline decoration-primary-300 underline-offset-2 transition-colors;
}


.prose p {
  @apply text-gray-800 dark:text-gray-200 leading-relaxed mt-4 mb-4;
}

.prose code {
  @apply bg-gray-100 dark:bg-gray-800 text-primary-700 dark:text-primary-300 px-1.5 py-0.5 rounded font-mono text-sm;
}

.prose pre {
  @apply bg-gray-100 dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-lg p-4 my-6 overflow-x-auto;
}

.prose pre code {
  @apply bg-transparent text-gray-900 dark:text-gray-100 px-0 py-0;
}

.prose strong {
  @apply text-gray-900 dark:text-white font-semibold;
}

.prose em {
  @apply text-gray-700 dark:text-gray-200;
}


.prose blockquote {
  @apply border-l-4 border-l-primary-500 dark:border-l-primary-400 text-gray-700 dark:text-gray-300 bg-gray-50 dark:bg-gray-800/50 px-5 py-4 rounded-r italic my-6;
}

.prose ul,
.prose ol {
  @apply my-5 pl-6;
}

.prose ul {
  @apply list-disc;
}

.prose ol {
  @apply list-decimal;
}

.prose li {
  @apply my-2;
}

.prose ul li,
.prose ol li {
  @apply text-gray-800 dark:text-gray-200;
}

.prose ul li::marker,
.prose ol li::marker {
  @apply text-primary-600 dark:text-primary-400;
}

.prose hr {
  @apply border-gray-300 dark:border-gray-700 my-10;
}

/* Table styles for documentation */
.prose table {
  @apply w-full border-collapse my-6;
}

.prose thead {
  @apply border-b-2 border-gray-300 dark:border-gray-700;
}

.prose th {
  @apply px-4 py-2 text-left font-semibold text-gray-900 dark:text-white;
}

.prose td {
  @apply px-4 py-2 border-t border-gray-200 dark:border-gray-700;
}

.prose tbody tr:hover {
  @apply bg-gray-50 dark:bg-gray-800;
}

.prose img,
.prose video,
.prose picture {
  @apply my-6 rounded-lg shadow-sm;
}

/* ===== GRADIENT UTILITY CLASSES ===== */

/* Hero/Landing Page Gradients - Layered radial approach */
.bg-gradient-hero {
  background:
    var(--gradient-hero-radial-1),
    var(--gradient-hero-radial-2),
    var(--gradient-hero-radial-3),
    var(--gradient-hero-radial-4),
    var(--gradient-hero-radial-5),
    var(--gradient-hero-base);
}

.bg-gradient-hero-vibrant {
  background: var(--gradient-hero-vibrant);
}

.bg-gradient-hero-subtle {
  background: var(--gradient-hero-subtle);
}

/* Accent Gradients */
.bg-gradient-accent-primary {
  background: var(--gradient-accent-primary);
}

.bg-gradient-accent-purple {
  background: var(--gradient-accent-purple);
}

.bg-gradient-accent-warm {
  background: var(--gradient-accent-warm);
}

/* Feature Section Gradients */
.bg-gradient-feature-light {
  background: var(--gradient-feature-light);
}

.bg-gradient-feature-card {
  background: var(--gradient-feature-card);
}

/* CTA/Interactive Gradients with Glow Effects */
.bg-gradient-cta {
  background: var(--gradient-cta);
  box-shadow: 0 0 20px rgba(0, 124, 137, 0.3);
  transition: all 0.3s ease;
}

.bg-gradient-cta:hover {
  background: var(--gradient-cta-hover);
  box-shadow: 0 0 40px rgba(0, 124, 137, 0.5);
  transform: translateY(-2px);
}

.bg-gradient-cta:active {
  transform: translateY(0);
  box-shadow: 0 0 30px rgba(0, 124, 137, 0.4);
}

/* Mesh/Complex Gradients */
.bg-gradient-mesh {
  background: var(--gradient-mesh);
}

/* Optional Mesh Overlay - Add subtle depth to any element */
.bg-gradient-mesh-overlay {
  position: relative;
}

.bg-gradient-mesh-overlay::before {
  content: '';
  position: absolute;
  inset: 0;
  background: var(--gradient-mesh-overlay);
  pointer-events: none;
  z-index: 1;
}

/* Text Gradients - utility classes for gradient text */
.text-gradient-primary {
  background: var(--gradient-accent-primary);
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
  color: transparent; /* Fallback */
}

.text-gradient-purple {
  background: var(--gradient-accent-purple);
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
  color: transparent; /* Fallback */
}

.text-gradient-warm {
  background: var(--gradient-accent-warm);
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
  color: transparent; /* Fallback */
}

/* Animated Gradients */
@keyframes gradientShift {
  0%, 100% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
}

.bg-gradient-animated {
  background-size: 200% 200%;
  animation: gradientShift 8s ease infinite;
}

  /* Responsive Gradient Utilities */
  @media (max-width: 768px) {
    .bg-gradient-hero,
    .bg-gradient-hero-vibrant {
      /* Simpler gradients on mobile for performance */
      background: var(--gradient-hero-subtle);
    }
  }
} /* End @layer components */

