/**
 * Socialaize Design System Components
 * 
 * A comprehensive collection of Vue components that follow
 * the design system tokens and patterns.
 */

// Base Components
export { default as Card } from './Card.vue';
export { default as Button } from './Button.vue';
export { default as Badge } from './Badge.vue';

// Enhanced Existing Components
export { default as BaseModal } from '../modals/BaseModal.vue';
export { default as TextInput } from '../inputs/TextInput.vue';
export { default as SelectDropdown } from '../inputs/SelectDropdown.vue';
export { default as PrimaryButton } from '../buttons/PrimaryButton.vue';
export { default as OutlineButton } from '../buttons/OutlineButton.vue';

// Component Type Definitions
export interface CardProps {
  title?: string;
  icon?: string;
  variant?: "default" | "primary" | "success" | "warning" | "error";
  size?: "sm" | "md" | "lg";
  padding?: "none" | "sm" | "md" | "lg";
  interactive?: boolean;
  glass?: "none" | "subtle" | "medium" | "strong";
  class?: string;
  titleClass?: string;
  contentClass?: string;
  iconClass?: string;
}

export interface ButtonProps {
  variant?: "primary" | "secondary" | "outline" | "ghost" | "danger" | "success" | "warning";
  size?: "xs" | "sm" | "md" | "lg" | "xl";
  text?: string;
  icon?: string;
  iconPosition?: "left" | "right";
  loading?: boolean;
  disabled?: boolean;
  as?: "button" | "a" | "router-link";
  type?: "button" | "submit" | "reset";
  href?: string;
  to?: string | object;
  class?: string;
  iconClass?: string;
  textClass?: string;
}

export interface BadgeProps {
  text?: string;
  icon?: string;
  variant?: "default" | "primary" | "secondary" | "success" | "warning" | "error" | "info";
  size?: "xs" | "sm" | "md" | "lg";
  interactive?: boolean;
  removable?: boolean;
  class?: string;
  iconClass?: string;
}

// Design System Token Utilities
export const designTokens = {
  colors: {
    primary: {
      50: 'var(--color-primary-50)',
      100: 'var(--color-primary-100)',
      200: 'var(--color-primary-200)',
      300: 'var(--color-primary-300)',
      400: 'var(--color-primary-400)',
      500: 'var(--color-primary-500)',
      600: 'var(--color-primary-600)',
      700: 'var(--color-primary-700)',
      800: 'var(--color-primary-800)',
      900: 'var(--color-primary-900)',
      950: 'var(--color-primary-950)',
    },
    success: {
      50: 'var(--color-success-50)',
      500: 'var(--color-success-500)',
      900: 'var(--color-success-900)',
    },
    warning: {
      50: 'var(--color-warning-50)',
      500: 'var(--color-warning-500)',
      900: 'var(--color-warning-900)',
    },
    error: {
      50: 'var(--color-error-50)',
      500: 'var(--color-error-500)',
      900: 'var(--color-error-900)',
    },
  },
  spacing: {
    xs: 'var(--spacing-1)',
    sm: 'var(--spacing-2)',
    md: 'var(--spacing-4)',
    lg: 'var(--spacing-6)',
    xl: 'var(--spacing-8)',
  },
  radius: {
    sm: 'var(--radius-sm)',
    md: 'var(--radius-md)',
    lg: 'var(--radius-lg)',
    xl: 'var(--radius-xl)',
    full: 'var(--radius-full)',
  },
  shadows: {
    sm: 'var(--shadow-sm)',
    md: 'var(--shadow-md)',
    lg: 'var(--shadow-lg)',
    xl: 'var(--shadow-xl)',
  },
  transitions: {
    fast: 'var(--transition-fast)',
    base: 'var(--transition-base)',
    medium: 'var(--transition-medium)',
    slow: 'var(--transition-slow)',
  },
};

// Component Size Presets
export const componentSizes = {
  button: {
    xs: 'btn-xs',
    sm: 'btn-sm',
    md: 'btn-md',
    lg: 'btn-lg',
    xl: 'btn-xl',
  },
  input: {
    sm: 'h-8',
    md: 'h-10',
    lg: 'h-12',
  },
  card: {
    sm: 'p-4',
    md: 'p-6',
    lg: 'p-8',
  },
};

// Utility Functions
export const getThemeClass = (isDark: boolean) => 
  isDark ? '[data-theme="dark"]' : '[data-theme="light"]';

export const getVariantClasses = (variant: string, type: 'button' | 'badge' | 'card' = 'button') => {
  const variantMaps: Record<'button' | 'badge' | 'card', Record<string, string>> = {
    button: {
      primary: 'bg-primary-500 hover:bg-primary-600 text-white',
      secondary: 'bg-gray-500 hover:bg-gray-600 text-white',
      outline: 'border-primary-500 text-primary-600 hover:bg-primary-500 hover:text-white',
      ghost: 'text-primary-600 hover:bg-primary-50',
      danger: 'bg-error-500 hover:bg-error-600 text-white',
      success: 'bg-success-500 hover:bg-success-600 text-white',
      warning: 'bg-warning-500 hover:bg-warning-600 text-white',
    },
    badge: {
      default: 'bg-gray-100 text-gray-800',
      primary: 'bg-primary-100 text-primary-800',
      success: 'bg-success-100 text-success-800',
      warning: 'bg-warning-100 text-warning-800',
      error: 'bg-error-100 text-error-800',
    },
    card: {
      default: 'bg-white border-gray-200',
      primary: 'bg-primary-50 border-primary-200',
      success: 'bg-success-50 border-success-200',
      warning: 'bg-warning-50 border-warning-200',
      error: 'bg-error-50 border-error-200',
    },
  };

  return variantMaps[type][variant] || ('default' in variantMaps[type] ? variantMaps[type].default : '') || '';
};

// CSS Class Utilities
export const focusRing = 'focus-ring';
export const transitionBase = 'transition-base';
export const glassMorphism = {
  subtle: 'glass-subtle',
  medium: 'glass-medium',
  strong: 'glass-strong',
};

// Animation Classes
export const animations = {
  fadeIn: 'animate-fade-in',
  fadeOut: 'animate-fade-out',
  slideInUp: 'animate-slide-in-up',
  slideInDown: 'animate-slide-in-down',
  scaleIn: 'animate-scale-in',
  scaleOut: 'animate-scale-out',
  pulse: 'animate-pulse',
  bounce: 'animate-bounce',
  spin: 'animate-spin',
};
