  1125→  }
  1126→
  1127→  @keyframes progress-indeterminate {
  1128→    0% {
  1129→      transform: translateX(-100%);
  1130→    }
  1131→    100% {
  1132→      transform: translateX(400%);
  1133→    }
  1134→  }
