   406→  .focus-ring-inset:focus-visible {
   407→    outline: none;
   408→    box-shadow: inset 0 0 0 2px var(--color-primary-500);
   409→  }
   410→
   411→  .focus-visible-only:focus:not(:focus-visible) {
   412→    outline: none;
   413→    box-shadow: none;
   414→  }
   415→
   416→  .focus-visible-only:focus-visible {
   417→    outline: none;
   418→    box-shadow: var(--shadow-focus-primary);
   419→  }
   420→
   421→  .focus-within-ring:focus-within {
   422→    outline: none;
   423→    box-shadow: var(--shadow-focus-primary);
   424→  }
   425→
   426→  .focus-none:focus,
   427→  .focus-none:focus-visible {
   428→    outline: none;
   429→    box-shadow: none;
   430→  }
   431→
   432→  /* ==========================================
   433→   * ACCESSIBILITY UTILITIES
   434→   * ========================================== */
   435→
   436→  .skip-link {
   437→    position: absolute;
   438→    left: -9999px;
   439→    z-index: 9999;
   440→    padding: var(--spacing-2) var(--spacing-4);
   441→    background-color: var(--color-primary-600);
   442→    color: #ffffff;
   443→    border-radius: var(--radius-md);
   444→    font-size: var(--font-size-sm);
   445→    font-weight: var(--font-weight-medium);
   446→    text-decoration: none;
   447→  }
   448→
   449→  .skip-link:focus,
   450→  .skip-link:focus-visible {
   451→    left: var(--spacing-4);
   452→    top: var(--spacing-4);
   453→    outline: none;
   454→    box-shadow: var(--shadow-focus-primary);
   455→  }
   456→
   457→  /* ==========================================
   458→   * PERFORMANCE & OPTIMIZATION UTILITIES
   459→   * ========================================== */
   460→
   461→  .scrollbar-hidden {
   462→    scrollbar-width: none;
   463→  }
   464→
   465→  .scrollbar-hidden::-webkit-scrollbar {
   466→    display: none;
   467→  }
   468→
   469→  .text-balance {
   470→    text-wrap: balance;
   471→  }
   472→
   473→  .content-auto {
   474→    content-visibility: auto;
   475→  }
   476→
   477→  .gpu-accelerate {
   478→    transform: translateZ(0);
   479→    backface-visibility: hidden;
   480→    perspective: 1000px;
   481→  }
   482→
   483→  /* ==========================================
   484→   * CONTAINER QUERY UTILITIES
   485→   * ========================================== */
   486→
   487→  .container-inline {
   488→    container-type: inline-size;
   489→  }
   490→
   491→  .container-normal {
   492→    container-type: normal;
   493→  }
   494→
   495→  .container-size {
   496→    container-type: size;
   497→  }
   498→
   499→  /* Named container contexts for specific use cases */
   500→  .container-card {
   501→    container-type: inline-size;
   502→    container-name: card;
   503→  }
   504→
   505→  .container-widget {
   506→    container-type: inline-size;
   507→    container-name: widget;
   508→  }
   509→
   510→  .container-section {
   511→    container-type: inline-size;
   512→    container-name: section;
   513→  }
   514→
   515→  .container-modal {
   516→    container-type: inline-size;
   517→    container-name: modal;
   518→  }
   519→
   520→  /* ==========================================
   521→   * MODERN DEPTH & ELEVATION SYSTEM
   522→   * Research-based 2024-2025 depth principles
   523→   * ========================================== */
   524→
   525→  /* Layered Shadow System - Combines ambient + contact shadows */
   526→  .shadow-depth-0 {
   527→    box-shadow: none;
   528→  }
   529→
   530→  .shadow-depth-1 {
   531→    box-shadow:
   532→      0px 1px 2px rgba(0, 0, 0, 0.06),
   533→      0px 0px 1px rgba(0, 0, 0, 0.08);
   534→  }
   535→
   536→  .shadow-depth-2 {
   537→    box-shadow:
   538→      0px 2px 4px rgba(0, 0, 0, 0.08),
   539→      0px 0px 2px rgba(0, 0, 0, 0.06);
   540→  }
   541→
   542→  .shadow-depth-4 {
   543→    box-shadow:
   544→      0px 4px 8px rgba(0, 0, 0, 0.10),
   545→      0px 0px 2px rgba(0, 0, 0, 0.06);
   546→  }
   547→
   548→  .shadow-depth-6 {
   549→    box-shadow:
   550→      0px 6px 12px rgba(0, 0, 0, 0.12),
   551→      0px 2px 4px rgba(0, 0, 0, 0.08);
   552→  }
   553→
   554→  .shadow-depth-8 {
   555→    box-shadow:
   556→      0px 8px 16px rgba(0, 0, 0, 0.14),
   557→      0px 2px 4px rgba(0, 0, 0, 0.08);
   558→  }
   559→
   560→  .shadow-depth-12 {
   561→    box-shadow:
   562→      0px 12px 24px rgba(0, 0, 0, 0.16),
   563→      0px 4px 8px rgba(0, 0, 0, 0.10);
   564→  }
   565→
   566→  .shadow-depth-16 {
   567→    box-shadow:
   568→      0px 16px 32px rgba(0, 0, 0, 0.18),
   569→      0px 4px 8px rgba(0, 0, 0, 0.10);
   570→  }
   571→
   572→  .shadow-depth-24 {
   573→    box-shadow:
   574→      0px 24px 48px rgba(0, 0, 0, 0.20),
   575→      0px 8px 16px rgba(0, 0, 0, 0.12);
   576→  }
   577→
   578→  /* Color-Matched Shadows - Theme-aware depth */
   579→  .shadow-primary {
   580→    box-shadow:
   581→      0px 4px 12px oklch(50% 0.16 200 / 0.15),
   582→      0px 0px 4px oklch(50% 0.16 200 / 0.10);
   583→  }
   584→
   585→  .shadow-success {
   586→    box-shadow:
   587→      0px 4px 12px oklch(65% 0.17 160 / 0.15),
   588→      0px 0px 4px oklch(65% 0.17 160 / 0.10);
   589→  }
   590→
   591→  .shadow-warning {
   592→    box-shadow:
   593→      0px 4px 12px oklch(75% 0.18 85 / 0.15),
   594→      0px 0px 4px oklch(75% 0.18 85 / 0.10);
   595→  }
   596→
   597→  .shadow-error {
   598→    box-shadow:
   599→      0px 4px 12px oklch(62% 0.22 25 / 0.15),
   600→      0px 0px 4px oklch(62% 0.22 25 / 0.10);
   601→  }
   602→
   603→  /* Dark mode enhanced depth shadows */
   604→  [data-theme="dark"] .shadow-depth-1 {
   605→    box-shadow:
   606→      0px 1px 2px rgba(0, 0, 0, 0.30),
   607→      0px 0px 1px rgba(0, 0, 0, 0.40);
   608→  }
   609→
   610→  [data-theme="dark"] .shadow-depth-2 {
   611→    box-shadow:
   612→      0px 2px 4px rgba(0, 0, 0, 0.35),
   613→      0px 0px 2px rgba(0, 0, 0, 0.30);
   614→  }
   615→
   616→  [data-theme="dark"] .shadow-depth-4 {
   617→    box-shadow:
   618→      0px 4px 8px rgba(0, 0, 0, 0.40),
   619→      0px 0px 2px rgba(0, 0, 0, 0.35);
   620→  }
   621→
   622→  [data-theme="dark"] .shadow-depth-6 {
   623→    box-shadow:
   624→      0px 6px 12px rgba(0, 0, 0, 0.45),
   625→      0px 2px 4px rgba(0, 0, 0, 0.35);
   626→  }
   627→
   628→  [data-theme="dark"] .shadow-depth-8 {
   629→    box-shadow:
   630→      0px 8px 16px rgba(0, 0, 0, 0.50),
   631→      0px 2px 4px rgba(0, 0, 0, 0.40);
   632→  }
   633→
   634→  [data-theme="dark"] .shadow-depth-12 {
   635→    box-shadow:
   636→      0px 12px 24px rgba(0, 0, 0, 0.55),
   637→      0px 4px 8px rgba(0, 0, 0, 0.45);
   638→  }
   639→
   640→  [data-theme="dark"] .shadow-depth-16 {
   641→    box-shadow:
   642→      0px 16px 32px rgba(0, 0, 0, 0.60),
   643→      0px 4px 8px rgba(0, 0, 0, 0.50);
   644→  }
   645→
   646→  [data-theme="dark"] .shadow-depth-24 {
   647→    box-shadow:
   648→      0px 24px 48px rgba(0, 0, 0, 0.70),
   649→      0px 8px 16px rgba(0, 0, 0, 0.55);
   650→  }
   651→
   652→  /* Neumorphism - Soft embossed/debossed effects */
   653→  .neomorph-flat {
   654→    background: var(--color-surface-primary);
   655→    box-shadow:
   656→      8px 8px 16px rgba(0, 0, 0, 0.12),
   657→      -8px -8px 16px rgba(255, 255, 255, 0.70);
   658→    border-radius: var(--radius-2xl);
   659→  }
   660→
   661→  [data-theme="dark"] .neomorph-flat {
   662→    background: var(--color-dark-surface-secondary);
   663→    box-shadow:
   664→      8px 8px 16px rgba(0, 0, 0, 0.40),
   665→      -8px -8px 16px rgba(55, 65, 81, 0.10);
   666→  }
   667→
   668→  .neomorph-pressed {
   669→    background: var(--color-surface-primary);
   670→    box-shadow:
   671→      inset 4px 4px 8px rgba(0, 0, 0, 0.12),
   672→      inset -4px -4px 8px rgba(255, 255, 255, 0.70);
   673→    border-radius: var(--radius-2xl);
   674→  }
   675→
   676→  [data-theme="dark"] .neomorph-pressed {
   677→    background: var(--color-dark-surface-secondary);
   678→    box-shadow:
   679→      inset 4px 4px 8px rgba(0, 0, 0, 0.40),
   680→      inset -4px -4px 8px rgba(55, 65, 81, 0.10);
   681→  }
   682→
   683→  .neomorph-raised {
   684→    background: var(--color-surface-primary);
   685→    box-shadow:
   686→      12px 12px 24px rgba(0, 0, 0, 0.14),
   687→      -12px -12px 24px rgba(255, 255, 255, 0.80);
   688→    border-radius: var(--radius-2xl);
   689→    transform: translateY(-2px);
   690→  }
   691→
   692→  [data-theme="dark"] .neomorph-raised {
   693→    background: var(--color-dark-surface-secondary);
   694→    box-shadow:
   695→      12px 12px 24px rgba(0, 0, 0, 0.50),
   696→      -12px -12px 24px rgba(55, 65, 81, 0.12);
   697→  }
   698→
   699→  /* Glassmorphism 2.0 - Enhanced with saturation */
   700→  .glass-vibrant {
   701→    background: rgba(255, 255, 255, 0.70);
   702→    backdrop-filter: blur(20px) saturate(180%);
   703→    -webkit-backdrop-filter: blur(20px) saturate(180%);
   704→    border: 1px solid rgba(255, 255, 255, 0.3);
   705→    box-shadow:
   706→      0 8px 32px rgba(0, 0, 0, 0.08),
   707→      inset 0 1px 0 rgba(255, 255, 255, 0.6);
   708→  }
   709→
   710→  [data-theme="dark"] .glass-vibrant {
   711→    background: rgba(31, 41, 55, 0.70);
   712→    border: 1px solid rgba(55, 65, 81, 0.4);
   713→    box-shadow:
   714→      0 8px 32px rgba(0, 0, 0, 0.30),
   715→      inset 0 1px 0 rgba(255, 255, 255, 0.1);
   716→  }
   717→
   718→  .glass-frosted {
   719→    background: rgba(255, 255, 255, 0.50);
   720→    backdrop-filter: blur(32px) saturate(200%);
   721→    -webkit-backdrop-filter: blur(32px) saturate(200%);
   722→    border: 1px solid rgba(255, 255, 255, 0.25);
   723→    box-shadow:
   724→      0 12px 48px rgba(0, 0, 0, 0.10),
   725→      inset 0 1px 0 rgba(255, 255, 255, 0.5);
   726→  }
   727→
   728→  [data-theme="dark"] .glass-frosted {
   729→    background: rgba(17, 24, 39, 0.50);
   730→    border: 1px solid rgba(55, 65, 81, 0.35);
   731→    box-shadow:
   732→      0 12px 48px rgba(0, 0, 0, 0.40),
   733→      inset 0 1px 0 rgba(255, 255, 255, 0.08);
   734→  }
   735→
   736→  /* Elevation Semantic Utilities */
   737→  .elevation-surface {
   738→    box-shadow: none;
   739→    position: relative;
   740→    z-index: 0;
   741→  }
   742→
   743→  .elevation-raised {
   744→    box-shadow:
   745→      0px 2px 4px rgba(0, 0, 0, 0.08),
   746→      0px 0px 2px rgba(0, 0, 0, 0.06);
   747→    position: relative;
   748→    z-index: 1;
   749→  }
   750→
   751→  .elevation-overlay {
   752→    box-shadow:
   753→      0px 4px 8px rgba(0, 0, 0, 0.10),
   754→      0px 0px 2px rgba(0, 0, 0, 0.06);
   755→    position: relative;
   756→    z-index: 10;
   757→  }
   758→
   759→  .elevation-floating {
   760→    box-shadow:
   761→      0px 8px 16px rgba(0, 0, 0, 0.14),
   762→      0px 2px 4px rgba(0, 0, 0, 0.08);
   763→    position: relative;
   764→    z-index: 50;
   765→  }
   766→
   767→  .elevation-modal {
   768→    box-shadow:
   769→      0px 16px 32px rgba(0, 0, 0, 0.18),
   770→      0px 4px 8px rgba(0, 0, 0, 0.10);
   771→    position: relative;
   772→    z-index: var(--z-index-modal);
   773→  }
   774→
   775→  /* Depth Layering - Overlapping elements */
   776→  .layer-back {
   777→    position: relative;
   778→    z-index: 1;
   779→    transform: translateY(8px);
   780→    opacity: 0.90;
   781→    filter: blur(0.5px);
   782→  }
   783→
   784→  .layer-middle {
   785→    position: relative;
   786→    z-index: 2;
   787→    transform: translateY(4px);
   788→    opacity: 0.95;
   789→  }
   790→
   791→  .layer-front {
   792→    position: relative;
   793→    z-index: 3;
   794→    transform: translateY(0px);
   795→  }
   796→
   797→  /* Depth through scale and contrast */
   798→  .depth-recede {
   799→    opacity: 0.80;
   800→    filter: brightness(0.95) saturate(0.90);
   801→    transform: scale(0.98);
   802→  }
   803→
   804→  .depth-neutral {
   805→    opacity: 1;
   806→    filter: none;
   807→    transform: scale(1);
   808→  }
   809→
   810→  .depth-advance {
   811→    opacity: 1;
   812→    filter: brightness(1.05) saturate(1.10);
   813→    transform: scale(1.02);
   814→  }
   815→
   816→  /* Depth transitions */
   817→  .depth-transition {
   818→    transition:
   819→      transform 300ms cubic-bezier(0, 0, 0.2, 1),
   820→      box-shadow 300ms cubic-bezier(0, 0, 0.2, 1),
   821→      opacity 300ms cubic-bezier(0, 0, 0.2, 1),
   822→      filter 300ms cubic-bezier(0, 0, 0.2, 1);
   823→  }
   824→
   825→  .depth-transition:hover {
   826→    transform: translateY(-4px) scale(1.02);
   827→  }
   828→
   829→  /* Reduce depth effects for accessibility */
   830→  @media (prefers-reduced-motion: reduce) {
   831→    .depth-transition {
   832→      transition: none;
   833→    }
   834→
   835→    .depth-transition:hover {
   836→      transform: none;
   837→    }
   838→
   839→    .layer-back,
   840→    .layer-middle,
   841→    .layer-front {
   842→      transform: none;
   843→      filter: none;
   844→    }
   845→  }
   846→
   847→  /* Performance optimization for mobile */
   848→  @media (max-width: 768px) {
   849→    .glass-vibrant,
   850→    .glass-frosted {
   851→      backdrop-filter: blur(12px);
   852→      -webkit-backdrop-filter: blur(12px);
   853→    }
   854→
   855→    .neomorph-flat,
   856→    .neomorph-pressed,
   857→    .neomorph-raised {
   858→      box-shadow:
   859→        4px 4px 8px rgba(0, 0, 0, 0.10),
   860→        -4px -4px 8px rgba(255, 255, 255, 0.50);
   861→    }
   862→  }
   863→}
