  }

  /* ==========================================
   * PERFORMANCE & OPTIMIZATION UTILITIES
   * ========================================== */

  .scrollbar-hidden {
    scrollbar-width: none;
  }

  .scrollbar-hidden::-webkit-scrollbar {
    display: none;
  }

  .text-balance {
    text-wrap: balance;
