  .content-auto {
    content-visibility: auto;
  }

  .gpu-accelerate {
    transform: translateZ(0);
    backface-visibility: hidden;
    perspective: 1000px;
  }

  /* ==========================================
   * CONTAINER QUERY UTILITIES
   * ========================================== */

  .container-inline {
    container-type: inline-size;
  }

  .container-normal {
    container-type: normal;
  }

  .container-size {
    container-type: size;
  }

  /* Named container contexts for specific use cases */
  .container-card {
    container-type: inline-size;
    container-name: card;
  }

  .container-widget {
    container-type: inline-size;
    container-name: widget;
  }

  .container-section {
    container-type: inline-size;
    container-name: section;
  }

  .container-modal {
    container-type: inline-size;
    container-name: modal;
  }

  /* ==========================================
   * MODERN DEPTH & ELEVATION SYSTEM
   * Research-based 2024-2025 depth principles
   * ========================================== */

  /* Layered Shadow System - Combines ambient + contact shadows */
  .shadow-depth-0 {
    box-shadow: none;
  }

  .shadow-depth-1 {
    box-shadow:
      0px 1px 2px rgba(0, 0, 0, 0.06),
      0px 0px 1px rgba(0, 0, 0, 0.08);
  }

  .shadow-depth-2 {
    box-shadow:
      0px 2px 4px rgba(0, 0, 0, 0.08),
      0px 0px 2px rgba(0, 0, 0, 0.06);
  }

  .shadow-depth-4 {
    box-shadow:
      0px 4px 8px rgba(0, 0, 0, 0.10),
      0px 0px 2px rgba(0, 0, 0, 0.06);
  }

  .shadow-depth-6 {
    box-shadow:
      0px 6px 12px rgba(0, 0, 0, 0.12),
      0px 2px 4px rgba(0, 0, 0, 0.08);
  }

  .shadow-depth-8 {
    box-shadow:
      0px 8px 16px rgba(0, 0, 0, 0.14),
      0px 2px 4px rgba(0, 0, 0, 0.08);
  }

  .shadow-depth-12 {
    box-shadow:
      0px 12px 24px rgba(0, 0, 0, 0.16),
      0px 4px 8px rgba(0, 0, 0, 0.10);
  }

  .shadow-depth-16 {
    box-shadow:
      0px 16px 32px rgba(0, 0, 0, 0.18),
      0px 4px 8px rgba(0, 0, 0, 0.10);
  }

  .shadow-depth-24 {
    box-shadow:
      0px 24px 48px rgba(0, 0, 0, 0.20),
      0px 8px 16px rgba(0, 0, 0, 0.12);
  }

  /* Color-Matched Shadows - Theme-aware depth */
  .shadow-primary {
    box-shadow:
      0px 4px 12px oklch(50% 0.16 200 / 0.15),
      0px 0px 4px oklch(50% 0.16 200 / 0.10);
  }

  .shadow-success {
    box-shadow:
      0px 4px 12px oklch(65% 0.17 160 / 0.15),
      0px 0px 4px oklch(65% 0.17 160 / 0.10);
  }

  .shadow-warning {
    box-shadow:
      0px 4px 12px oklch(75% 0.18 85 / 0.15),
      0px 0px 4px oklch(75% 0.18 85 / 0.10);
  }

  .shadow-error {
    box-shadow:
      0px 4px 12px oklch(62% 0.22 25 / 0.15),
      0px 0px 4px oklch(62% 0.22 25 / 0.10);
  }

  /* Dark mode enhanced depth shadows */
  [data-theme="dark"] .shadow-depth-1 {
    box-shadow:
      0px 1px 2px rgba(0, 0, 0, 0.30),
      0px 0px 1px rgba(0, 0, 0, 0.40);
  }

  [data-theme="dark"] .shadow-depth-2 {
    box-shadow:
      0px 2px 4px rgba(0, 0, 0, 0.35),
      0px 0px 2px rgba(0, 0, 0, 0.30);
  }

  [data-theme="dark"] .shadow-depth-4 {
    box-shadow:
      0px 4px 8px rgba(0, 0, 0, 0.40),
      0px 0px 2px rgba(0, 0, 0, 0.35);
  }

  [data-theme="dark"] .shadow-depth-6 {
    box-shadow:
      0px 6px 12px rgba(0, 0, 0, 0.45),
      0px 2px 4px rgba(0, 0, 0, 0.35);
  }

  [data-theme="dark"] .shadow-depth-8 {
    box-shadow:
      0px 8px 16px rgba(0, 0, 0, 0.50),
      0px 2px 4px rgba(0, 0, 0, 0.40);
  }

  [data-theme="dark"] .shadow-depth-12 {
    box-shadow:
      0px 12px 24px rgba(0, 0, 0, 0.55),
      0px 4px 8px rgba(0, 0, 0, 0.45);
  }

  [data-theme="dark"] .shadow-depth-16 {
    box-shadow:
      0px 16px 32px rgba(0, 0, 0, 0.60),
      0px 4px 8px rgba(0, 0, 0, 0.50);
  }

  [data-theme="dark"] .shadow-depth-24 {
    box-shadow:
      0px 24px 48px rgba(0, 0, 0, 0.70),
      0px 8px 16px rgba(0, 0, 0, 0.55);
  }

  /* Neumorphism - Soft embossed/debossed effects */
  .neomorph-flat {
    background: var(--color-surface-primary);
    box-shadow:
      8px 8px 16px rgba(0, 0, 0, 0.12),
      -8px -8px 16px rgba(255, 255, 255, 0.70);
    border-radius: var(--radius-2xl);
  }

  [data-theme="dark"] .neomorph-flat {
    background: var(--color-dark-surface-secondary);
    box-shadow:
      8px 8px 16px rgba(0, 0, 0, 0.40),
      -8px -8px 16px rgba(55, 65, 81, 0.10);
  }

  .neomorph-pressed {
    background: var(--color-surface-primary);
    box-shadow:
      inset 4px 4px 8px rgba(0, 0, 0, 0.12),
      inset -4px -4px 8px rgba(255, 255, 255, 0.70);
    border-radius: var(--radius-2xl);
  }

  [data-theme="dark"] .neomorph-pressed {
    background: var(--color-dark-surface-secondary);
    box-shadow:
      inset 4px 4px 8px rgba(0, 0, 0, 0.40),
      inset -4px -4px 8px rgba(55, 65, 81, 0.10);
  }

  .neomorph-raised {
    background: var(--color-surface-primary);
    box-shadow:
      12px 12px 24px rgba(0, 0, 0, 0.14),
      -12px -12px 24px rgba(255, 255, 255, 0.80);
    border-radius: var(--radius-2xl);
    transform: translateY(-2px);
  }

  [data-theme="dark"] .neomorph-raised {
    background: var(--color-dark-surface-secondary);
    box-shadow:
      12px 12px 24px rgba(0, 0, 0, 0.50),
      -12px -12px 24px rgba(55, 65, 81, 0.12);
  }

  /* Glassmorphism 2.0 - Enhanced with saturation */
  .glass-vibrant {
    background: rgba(255, 255, 255, 0.70);
    backdrop-filter: blur(20px) saturate(180%);
    -webkit-backdrop-filter: blur(20px) saturate(180%);
    border: 1px solid rgba(255, 255, 255, 0.3);
    box-shadow:
      0 8px 32px rgba(0, 0, 0, 0.08),
      inset 0 1px 0 rgba(255, 255, 255, 0.6);
  }

  [data-theme="dark"] .glass-vibrant {
    background: rgba(31, 41, 55, 0.70);
    border: 1px solid rgba(55, 65, 81, 0.4);
    box-shadow:
      0 8px 32px rgba(0, 0, 0, 0.30),
      inset 0 1px 0 rgba(255, 255, 255, 0.1);
  }

  .glass-frosted {
    background: rgba(255, 255, 255, 0.50);
    backdrop-filter: blur(32px) saturate(200%);
    -webkit-backdrop-filter: blur(32px) saturate(200%);
    border: 1px solid rgba(255, 255, 255, 0.25);
    box-shadow:
      0 12px 48px rgba(0, 0, 0, 0.10),
      inset 0 1px 0 rgba(255, 255, 255, 0.5);
  }

  [data-theme="dark"] .glass-frosted {
    background: rgba(17, 24, 39, 0.50);
    border: 1px solid rgba(55, 65, 81, 0.35);
    box-shadow:
      0 12px 48px rgba(0, 0, 0, 0.40),
      inset 0 1px 0 rgba(255, 255, 255, 0.08);
  }

  /* Elevation Semantic Utilities */
  .elevation-surface {
    box-shadow: none;
    position: relative;
    z-index: 0;
  }

  .elevation-raised {
    box-shadow:
      0px 2px 4px rgba(0, 0, 0, 0.08),
      0px 0px 2px rgba(0, 0, 0, 0.06);
    position: relative;
    z-index: 1;
  }

  .elevation-overlay {
    box-shadow:
      0px 4px 8px rgba(0, 0, 0, 0.10),
      0px 0px 2px rgba(0, 0, 0, 0.06);
    position: relative;
    z-index: 10;
  }

  .elevation-floating {
    box-shadow:
      0px 8px 16px rgba(0, 0, 0, 0.14),
      0px 2px 4px rgba(0, 0, 0, 0.08);
    position: relative;
    z-index: 50;
  }

  .elevation-modal {
    box-shadow:
      0px 16px 32px rgba(0, 0, 0, 0.18),
      0px 4px 8px rgba(0, 0, 0, 0.10);
    position: relative;
    z-index: var(--z-index-modal);
  }

  /* Depth Layering - Overlapping elements */
  .layer-back {
    position: relative;
    z-index: 1;
    transform: translateY(8px);
    opacity: 0.90;
    filter: blur(0.5px);
  }

  .layer-middle {
    position: relative;
    z-index: 2;
    transform: translateY(4px);
    opacity: 0.95;
  }

  .layer-front {
    position: relative;
    z-index: 3;
    transform: translateY(0px);
  }

  /* Depth through scale and contrast */
  .depth-recede {
    opacity: 0.80;
    filter: brightness(0.95) saturate(0.90);
    transform: scale(0.98);
  }

  .depth-neutral {
    opacity: 1;
    filter: none;
    transform: scale(1);
  }

  .depth-advance {
    opacity: 1;
    filter: brightness(1.05) saturate(1.10);
    transform: scale(1.02);
  }

  /* Depth transitions */
  .depth-transition {
    transition:
      transform 300ms cubic-bezier(0, 0, 0.2, 1),
      box-shadow 300ms cubic-bezier(0, 0, 0.2, 1),
      opacity 300ms cubic-bezier(0, 0, 0.2, 1),
      filter 300ms cubic-bezier(0, 0, 0.2, 1);
  }

  .depth-transition:hover {
    transform: translateY(-4px) scale(1.02);
  }

  /* Reduce depth effects for accessibility */
  @media (prefers-reduced-motion: reduce) {
    .depth-transition {
      transition: none;
    }

    .depth-transition:hover {
      transform: none;
    }

    .layer-back,
    .layer-middle,
    .layer-front {
      transform: none;
      filter: none;
    }
  }

  /* Performance optimization for mobile */
  @media (max-width: 768px) {
    .glass-vibrant,
    .glass-frosted {
      backdrop-filter: blur(12px);
      -webkit-backdrop-filter: blur(12px);
    }

    .neomorph-flat,
    .neomorph-pressed,
    .neomorph-raised {
      box-shadow:
        4px 4px 8px rgba(0, 0, 0, 0.10),
        -4px -4px 8px rgba(255, 255, 255, 0.50);
    }
  }
}

