  /* Durations - synced with constants/animations.ts */
  --duration-instant: 100ms;
  --duration-fast: 200ms;
  --duration-normal: 300ms;
  --duration-slow: 500ms;
  --duration-slower: 800ms;
  --duration-celebration: 2000ms;

  /* ===== Z-INDEX SCALE ===== */

  --z-dropdown: 1000;
  --z-sticky: 1020;
  --z-modal: 1050;
  --z-tooltip: 1070;
  --z-toast: 1080;
}

/* ===== DARK MODE OVERRIDES ===== */

@theme dark {
  /* Adjust primary color slightly lighter in dark mode for better visibility */
  --color-primary-500: oklch(55% 0.18 200);
}

/* ============================================
   UTILITY CLASSES
   ============================================ */

.label-position {
  top: min(min(-1.5vh, -1.5vw), -1.5rem);
}

/* Gradient text utilities (from typography.css) */
.gradient-text-primary {
  background: linear-gradient(135deg, #007c89 0%, #7c3aed 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.gradient-text-light {
  background: linear-gradient(135deg, rgb(17 24 39) 0%, rgb(75 85 99) 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

[data-theme="dark"] .gradient-text-light {
  background: linear-gradient(135deg, white 0%, #d1d5db 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/* Prose styles now handled by @tailwindcss/typography plugin
 * Use class="prose dark:prose-invert" in HTML
 * Customize in @theme if needed */

