index 8e755934..2717304e 100644
--- a/src/styles/global.css
+++ b/src/styles/global.css
@@ -123,8 +123,43 @@
   --duration-slower: 800ms;
   --duration-celebration: 2000ms;

+  /* ===== ANIMATION KEYFRAMES ===== */
+
+  /* Predefined Animations */
+  --animate-fade-in: fade-in var(--duration-normal) var(--ease-smooth);
+  --animate-fade-out: fade-out var(--duration-normal) var(--ease-smooth);
+  --animate-slide-in-up: slide-in-up var(--duration-slow) var(--ease-smooth);
+  --animate-slide-in-down: slide-in-down var(--duration-slow) var(--ease-smooth);
+  --animate-scale-in: scale-in var(--duration-normal) var(--ease-smooth);
+  --animate-scale-out: scale-out var(--duration-normal) var(--ease-smooth);
+  --animate-pulse: pulse 2s var(--ease-smooth) infinite;
+  --animate-bounce: bounce 1s infinite;
+  --animate-spin: spin 1s linear infinite;
+  --animate-ping: ping 1s var(--ease-smooth) infinite;
+
+  /* ===== BOX SHADOWS ===== */
+
+  --shadow-xs: 0 1px 2px 0 rgb(0 0 0 / 0.05);
+  --shadow-sm: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
+  --shadow-DEFAULT: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
+  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
+  --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
+  --shadow-xl: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1);
+  --shadow-2xl: 0 25px 50px -12px rgb(0 0 0 / 0.25);
+  --shadow-inner: inset 0 2px 4px 0 rgb(0 0 0 / 0.05);
+  --shadow-focus-primary: 0 0 0 3px rgb(0 124 137 / 0.1);
+  --shadow-focus-error: 0 0 0 3px rgb(239 68 68 / 0.1);
+  --shadow-focus-success: 0 0 0 3px rgb(16 185 129 / 0.1);
+
   /* ===== Z-INDEX SCALE ===== */

+  --z-behind: -1;
+  --z-0: 0;
+  --z-10: 10;
+  --z-20: 20;
+  --z-30: 30;
+  --z-40: 40;
+  --z-50: 50;
   --z-dropdown: 1000;
   --z-sticky: 1020;
   --z-modal: 1050;

