import { computed, reactive, watchEffect } from 'vue';
import {
  usePreferredReducedMotion,
  useNetwork,
  useBattery,
  useMounted,
  useFps,
  useIdle,
  usePageLeave,
  type UseIdleOptions
} from '@vueuse/core';

interface MeasureHandle {
  end: () => void;
}

interface UseAnimationEnvironmentOptions {
  /**
   * Enable FPS monitoring (default: true)
   * Set to false to disable FPS tracking for better performance
   */
  enableFpsMonitoring?: boolean;

  /**
   * FPS threshold for quality reduction (default: 30)
   * When FPS drops below this, optimizations.lowFps becomes true
   */
  fpsThreshold?: number;

  /**
   * Enable idle detection (default: true)
   * Set to false to disable user inactivity tracking
   */
  enableIdleDetection?: boolean;

  /**
   * Idle timeout in milliseconds (default: 5 minutes)
   */
  idleTimeout?: number;

  /**
   * Custom idle detection options
   */
  idleOptions?: UseIdleOptions;

  /**
   * Enable page leave detection (default: true)
   */
  enablePageLeaveDetection?: boolean;

  /**
   * Battery level threshold for optimization (default: 0.2 = 20%)
   */
  batteryThreshold?: number;
}

export const useAnimationEnvironment = (options: UseAnimationEnvironmentOptions = {}) => {
  const {
    enableFpsMonitoring = true,
    fpsThreshold = 30,
    enableIdleDetection = true,
    idleTimeout = 5 * 60 * 1000, // 5 minutes
    idleOptions,
    enablePageLeaveDetection = true,
    batteryThreshold = 0.2
  } = options;

  const isMounted = useMounted();
  const prefersReducedMotion = usePreferredReducedMotion();
  const networkInfo = useNetwork();
  const batteryInfo = useBattery();

  // Performance monitoring
  const fps = enableFpsMonitoring ? useFps({ every: 10 }) : computed(() => 60);

  // User activity tracking
  const { idle, lastActive, reset: resetIdle } = enableIdleDetection
    ? useIdle(idleTimeout, idleOptions)
    : { idle: computed(() => false), lastActive: computed(() => 0), reset: () => {} };

  // Page leave detection
  const isPageLeft = enablePageLeaveDetection
    ? usePageLeave()
    : computed(() => false);

  const optimizations = reactive({
    // Motion preferences
    reducedMotion: false,

    // Battery optimization
    batteryOptimization: false,

    // Performance optimization
    lowFps: false,

    // User activity
    userIdle: false,
    pageLeft: false,

    // Legacy optimizations
    lazyLoading: true,
    imageOptimization: true,
    codesplitting: true,
    serviceWorker: false,
    memoryManagement: true,
  });

  watchEffect(() => {
    // Reduced motion from accessibility preferences
    optimizations.reducedMotion = isMounted.value && prefersReducedMotion.value === 'reduce';

    // Battery optimization when low battery and not charging
    const level = batteryInfo.level.value;
    optimizations.batteryOptimization = typeof level === 'number'
      ? level < batteryThreshold && !batteryInfo.charging.value
      : false;

    // FPS-based optimization
    optimizations.lowFps = enableFpsMonitoring && fps.value < fpsThreshold;

    // User activity
    optimizations.userIdle = enableIdleDetection && idle.value;
    optimizations.pageLeft = enablePageLeaveDetection && isPageLeft.value;

    optimizations.serviceWorker = false;
  });

  /**
   * Computed flag indicating if animations should be paused
   * True when: user is idle, page is left, or battery is critically low
   */
  const shouldPauseAnimations = computed(() =>
    optimizations.userIdle ||
    optimizations.pageLeft ||
    (optimizations.batteryOptimization && batteryInfo.level.value < 0.1) // Critical battery
  );

  /**
   * Computed flag indicating if animations should run
   * Respects reduced motion, battery optimization, and low FPS
   */
  const canStartAnimation = computed(() =>
    !optimizations.reducedMotion &&
    !optimizations.batteryOptimization &&
    !optimizations.lowFps
  );

  /**
   * Computed animation quality level based on current constraints
   * - 'high': No constraints, full animations
   * - 'medium': Low FPS or slow network, simplified animations
   * - 'low': Battery optimization, minimal animations
   * - 'none': Reduced motion preference, no animations
   */
  const animationQuality = computed<'none' | 'low' | 'medium' | 'high'>(() => {
    if (optimizations.reducedMotion) return 'none';
    if (optimizations.batteryOptimization) return 'low';
    if (optimizations.lowFps || !networkInfo.isOnline.value) return 'medium';
    return 'high';
  });

  /**
   * Get recommended animation duration based on current quality
   */
  const getRecommendedDuration = (baseDuration: number): number => {
    switch (animationQuality.value) {
      case 'none': return 0;
      case 'low': return baseDuration * 0.3;
      case 'medium': return baseDuration * 0.6;
      case 'high': return baseDuration;
    }
  };

  const windowRef = computed(() => (isMounted.value ? window : null));

  const measureOperation = (_name?: string): MeasureHandle => {
    if (!isMounted.value || !windowRef.value) {
      return { end: () => {} };
    }

    const start = windowRef.value.performance.now();
    return {
      end: () => {
        if (!windowRef.value) return;
        windowRef.value.performance.now() - start;
      },
    };
  };

  return {
    // Optimization flags
    optimizations,

    // Computed helpers
    shouldPauseAnimations,
    canStartAnimation,
    animationQuality,
    getRecommendedDuration,

    // Raw data
    fps: enableFpsMonitoring ? fps : null,
    idle: enableIdleDetection ? { idle, lastActive, reset: resetIdle } : null,
    isPageLeft: enablePageLeaveDetection ? isPageLeft : null,
    networkInfo,
    batteryInfo,
    prefersReducedMotion,

    // Utilities
    windowRef,
    measureOperation,
  };
};

