   700→## 2. FORM STATES & VALIDATION
   701→
   702→### Field States
   703→
   704→#### Complete State Diagram
   705→```css
   706→/* Default (empty, unfocused) */
   707→.input {
   708→  background-color: var(--surface-primary);
   709→  border-color: var(--border-color);
   710→  color: var(--text-primary);
   711→
   712→  &::placeholder {
   713→    color: var(--text-placeholder);
   714→  }
   715→}
   716→
   717→/* Focus (user interaction) */
   718→.input:focus {
   719→  border-color: var(--primary-500);
   720→  background-color: var(--surface-primary);
   721→  box-shadow: inset 0 0 0 1px var(--primary-500);
   722→}
   723→
   724→.input:focus-visible {
   725→  outline: 2px solid var(--focus-color);
   726→  outline-offset: 2px;
   727→}
   728→
   729→/* Filled (has value) */
   730→.input:not(:placeholder-shown) {
   731→  /* Optional: subtle style change when filled */
   732→}
   733→
   734→/* Disabled (not editable) */
   735→.input:disabled {
   736→  background-color: var(--surface-secondary);
   737→  color: var(--text-tertiary);
   738→  cursor: not-allowed;
   739→  opacity: 0.6;
   740→}
   741→
   742→/* Readonly (visible but not editable) */
   743→.input:read-only {
   744→  background-color: var(--surface-secondary);
   745→  cursor: default;
   746→  border-style: dotted;
   747→}
   748→
   749→/* Error state */
   750→.input.error,
   751→.input[aria-invalid="true"] {
   752→  border-color: var(--error-500);
   753→  background-color: rgba(220, 38, 38, 0.05);
   754→
   755→  &:focus-visible {
   756→    box-shadow: 0 0 0 3px rgba(220, 38, 38, 0.1);
   757→  }
   758→}
   759→
   760→/* Success state */
   761→.input.success,
   762→.input[aria-invalid="false"] {
   763→  border-color: var(--success-500);
   764→
   765→  &::after {
   766→    content: "✓";
   767→    position: absolute;
   768→    right: 0.75rem;
   769→    color: var(--success-500);
   770→  }
   771→}
   772→
   773→/* Loading state (async validation) */
   774→.input.loading {
   775→  background-image: linear-gradient(
   776→    90deg,
   777→    transparent 0%,
   778→    rgba(0, 124, 137, 0.1) 50%,
   779→    transparent 100%
   780→  );
   781→  background-size: 200% 100%;
   782→  animation: shimmer 1.5s infinite;
   783→}
   784→
   785→@keyframes shimmer {
   786→  0% { background-position: -200% 0; }
   787→  100% { background-position: 200% 0; }
   788→}
