  1659→
  1660→```javascript
  1661→class Autocomplete {
  1662→  constructor(input, options) {
  1663→    this.input = input;
  1664→    this.options = options;
  1665→    this.optionsList = input.parentElement.querySelector('[role="listbox"]');
  1666→    this.highlightedIndex = -1;
  1667→
  1668→    this.input.addEventListener('input', (e) => this.filterOptions(e.target.value));
  1669→    this.input.addEventListener('keydown', (e) => this.handleKeyboard(e));
  1670→    this.input.addEventListener('blur', () => this.closeOptions());
  1671→  }
  1672→
  1673→  filterOptions(query) {
  1674→    const filtered = this.options.filter(opt =>
  1675→      opt.toLowerCase().includes(query.toLowerCase())
  1676→    );
  1677→
  1678→    this.renderOptions(filtered, query);
  1679→
  1680→    if (filtered.length > 0) {
  1681→      this.optionsList.hidden = false;
  1682→      this.input.setAttribute('aria-expanded', 'true');
  1683→    } else {
  1684→      this.closeOptions();
  1685→    }
  1686→  }
  1687→
  1688→  renderOptions(filtered, query) {
  1689→    this.optionsList.innerHTML = filtered
  1690→      .map((option, index) => {
  1691→        const highlighted = query
  1692→          ? option.replace(
  1693→              new RegExp(`(${query})`, 'gi'),
  1694→              '<strong>$1</strong>'
  1695→            )
  1696→          : option;
  1697→
  1698→        return `
  1699→          <li
  1700→            role="option"
  1701→            class="autocomplete__option ${index === this.highlightedIndex ? 'highlighted' : ''}"
  1702→            data-value="${option}"
  1703→          >
  1704→            ${highlighted}
  1705→          </li>
  1706→        `;
  1707→      })
  1708→      .join('');
  1709→
  1710→    this.optionsList.querySelectorAll('[role="option"]').forEach((opt, index) => {
  1711→      opt.addEventListener('click', () => this.selectOption(opt.dataset.value));
  1712→
  1713→      if (index === this.highlightedIndex) {
  1714→        opt.classList.add('highlighted');
  1715→        opt.scrollIntoView({ block: 'nearest' });
  1716→      }
  1717→    });
  1718→  }
  1719→
  1720→  handleKeyboard(e) {
  1721→    const options = this.optionsList.querySelectorAll('[role="option"]');
  1722→
  1723→    switch (e.key) {
  1724→      case 'ArrowDown':
  1725→        e.preventDefault();
  1726→        this.highlightedIndex = Math.min(this.highlightedIndex + 1, options.length - 1);
  1727→        this.renderOptions(Array.from(options).map(o => o.dataset.value), this.input.value);
  1728→        break;
  1729→
  1730→      case 'ArrowUp':
  1731→        e.preventDefault();
  1732→        this.highlightedIndex = Math.max(this.highlightedIndex - 1, -1);
  1733→        this.renderOptions(Array.from(options).map(o => o.dataset.value), this.input.value);
  1734→        break;
  1735→
  1736→      case 'Enter':
  1737→        e.preventDefault();
  1738→        if (this.highlightedIndex >= 0) {
  1739→          this.selectOption(options[this.highlightedIndex].dataset.value);
  1740→        }
  1741→        break;
  1742→
  1743→      case 'Escape':
  1744→        this.closeOptions();
  1745→        break;
  1746→    }
  1747→  }
  1748→
  1749→  selectOption(value) {
  1750→    this.input.value = value;
  1751→    this.closeOptions();
  1752→    this.input.dispatchEvent(new Event('change', { bubbles: true }));
  1753→  }
  1754→
  1755→  closeOptions() {
  1756→    this.optionsList.hidden = true;
  1757→    this.input.setAttribute('aria-expanded', 'false');
  1758→    this.highlightedIndex = -1;
  1759→  }
  1760→}
  1761→
  1762→const countries = ['United States', 'Canada', 'Mexico', 'United Kingdom', ...];
  1763→new Autocomplete(document.getElementById('country'), countries);
  1764→```
