  2154→
  2155→/* Custom checkbox dark mode */
  2156→.form-control__input {
  2157→  background-color: var(--input-bg);
  2158→  border-color: var(--border-color);
  2159→
  2160→  &:checked {
  2161→    background-color: var(--primary-500);
  2162→    border-color: var(--primary-500);
  2163→  }
  2164→}
  2165→
  2166→/* Select dropdown dark mode */
  2167→select {
  2168→  background-image: url("data:image/svg+xml;charset=UTF-8,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='none' stroke='currentColor' stroke-width='2'%3e%3cpolyline points='6 9 12 15 18 9'%3e%3c/polyline%3e%3c/svg%3e");
  2169→  background-repeat: no-repeat;
