   791→      toastRef.value?.addToast({ type: 'success', message, title }),
   792→
   793→    error: (message: string, title?: string) =>
   794→      toastRef.value?.addToast({ type: 'error', message, title }),
   795→
   796→    warning: (message: string, title?: string) =>
   797→      toastRef.value?.addToast({ type: 'warning', message, title }),
   798→
   799→    info: (message: string, title?: string) =>
   800→      toastRef.value?.addToast({ type: 'info', message, title })
   801→  }
   802→}
