   881→          v-for="req in passwordRequirements"
   882→          :key="req.id"
   883→          class="flex items-center gap-2 text-sm transition-colors"
   884→          :class="req.met ? 'text-success-600' : 'text-secondary'"
   885→        >
   886→          <Icon
   887→            :icon="req.met ? 'mdi:check-circle' : 'mdi:circle-outline'"
   888→            class="w-4 h-4"
   889→          />
