   899→      :class="{
   900→        'bg-primary-500 text-white hover:bg-primary-600 active:scale-95': isFormValid && !isSubmitting,
   901→        'bg-surface-tertiary text-tertiary cursor-not-allowed opacity-50': !isFormValid || isSubmitting
   902→      }"
   903→    >
   904→      {{ isSubmitting ? 'Submitting...' : 'Sign Up' }}
   905→    </button>
   906→  </form>
   907→</template>
   908→
   909→<script setup lang="ts">
   910→import { ref, computed } from 'vue'
   911→import { Icon } from '@iconify/vue'
   912→import { useToast } from '@/composables/useToast'
   913→
   914→const { success: successToast, error: errorToast } = useToast()
   915→
   916→const form = ref({
   917→  email: '',
   918→  password: ''
   919→})
   920→
   921→const validFields = ref<Record<string, boolean>>({
   922→  email: false,
   923→  password: false
   924→})
   925→
   926→const errors = ref<Record<string, string>>({
   927→  email: '',
   928→  password: ''
   929→})
   930→
   931→const isSubmitting = ref(false)
   932→
   933→const passwordRequirements = computed(() => [
   934→  {
   935→    id: 'length',
   936→    label: 'At least 8 characters',
   937→    met: form.value.password.length >= 8
   938→  },
   939→  {
   940→    id: 'uppercase',
   941→    label: 'One uppercase letter',
   942→    met: /[A-Z]/.test(form.value.password)
   943→  },
   944→  {
   945→    id: 'lowercase',
   946→    label: 'One lowercase letter',
   947→    met: /[a-z]/.test(form.value.password)
   948→  },
   949→  {
   950→    id: 'number',
   951→    label: 'One number',
   952→    met: /\d/.test(form.value.password)
   953→  }
   954→])
   955→
   956→const isFormValid = computed(
   957→  () => Object.values(errors.value).every(e => !e) &&
   958→        Object.values(validFields.value).every(v => v)
   959→)
   960→
   961→const validateField = (field: 'email' | 'password') => {
   962→  if (field === 'email') {
   963→    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
   964→    if (!form.value.email) {
   965→      errors.value.email = 'Email is required'
   966→      validFields.value.email = false
   967→    } else if (!emailRegex.test(form.value.email)) {
   968→      errors.value.email = 'Invalid email format'
   969→      validFields.value.email = false
   970→    } else {
   971→      errors.value.email = ''
   972→      validFields.value.email = true
   973→    }
   974→  } else if (field === 'password') {
   975→    const allMet = passwordRequirements.value.every(r => r.met)
   976→    errors.value.password = allMet ? '' : 'Password does not meet requirements'
   977→    validFields.value.password = allMet
   978→  }
   979→}
   980→
   981→const submit = async () => {
   982→  isSubmitting.value = true
   983→  try {
   984→    // API call
   985→    await new Promise(resolve => setTimeout(resolve, 2000))
   986→    successToast('Account created successfully!')
   987→  } catch (error) {
   988→    errorToast('Failed to create account', 'Error')
   989→  } finally {
   990→    isSubmitting.value = false
   991→  }
   992→}
   993→</script>
   994→```
