  1044→interface Props {
  1045→  label: string
  1046→  icon?: string
  1047→  variant?: 'primary' | 'secondary' | 'outline' | 'ghost'
  1048→  size?: 'xs' | 'sm' | 'md' | 'lg'
  1049→  isLoading?: boolean
  1050→  isDisabled?: boolean
  1051→  onClick?: () => Promise<void>
  1052→}
  1053→
  1054→const props = withDefaults(defineProps<Props>(), {
  1055→  variant: 'primary',
  1056→  size: 'md'
  1057→})
  1058→
  1059→const isLoading = ref(props.isLoading)
  1060→const isDisabled = ref(props.isDisabled)
  1061→const ripples = ref<Array<{ id: string; x: number; y: number; size: number }>>([])
  1062→
  1063→const getButtonClass = () => {
  1064→  const variants = {
  1065→    primary: 'bg-primary-500 text-white hover:bg-primary-600 active:scale-95 disabled:opacity-60',
  1066→    secondary: 'bg-surface-secondary text-primary hover:bg-surface-tertiary active:scale-95',
  1067→    outline: 'border border-primary-500 text-primary-500 hover:bg-primary-50 active:scale-95',
  1068→    ghost: 'text-primary-500 hover:bg-surface-secondary active:scale-95'
  1069→  }
  1070→
  1071→  const sizes = {
  1072→    xs: 'text-xs px-2 py-1',
  1073→    sm: 'text-sm px-3 py-1.5',
  1074→    md: 'text-base px-4 py-2',
  1075→    lg: 'text-lg px-6 py-3'
  1076→  }
  1077→
  1078→  return `${variants[props.variant]} ${sizes[props.size]}`
  1079→}
  1080→
  1081→const showRipple = (event: MouseEvent) => {
  1082→  const button = event.currentTarget as HTMLElement
  1083→  const rect = button.getBoundingClientRect()
  1084→  const x = event.clientX - rect.left
  1085→  const y = event.clientY - rect.top
  1086→
  1087→  const ripple = {
  1088→    id: Math.random().toString(36).substr(2, 9),
  1089→    x,
  1090→    y,
  1091→    size: 0
  1092→  }
  1093→
  1094→  ripples.value.push(ripple)
  1095→
  1096→  // Animate ripple
  1097→  let size = 0
  1098→  const interval = setInterval(() => {
  1099→    size += 5
  1100→    ripple.size = size
  1101→
  1102→    if (size > 100) {
  1103→      clearInterval(interval)
  1104→      ripples.value = ripples.value.filter(r => r.id !== ripple.id)
  1105→    }
  1106→  }, 16)
  1107→}
  1108→
  1109→const handleClick = async () => {
  1110→  if (props.onClick) {
  1111→    isLoading.value = true
  1112→    try {
  1113→      await props.onClick()
  1114→    } finally {
  1115→      isLoading.value = false
  1116→    }
  1117→  }
  1118→}
  1119→</script>
  1120→
  1121→<style scoped>
  1122→@keyframes ripple {
  1123→  to {
  1124→    transform: scale(4);
  1125→    opacity: 0;
  1126→  }
  1127→}
  1128→
  1129→.animate-ripple {
  1130→  animation: ripple 600ms ease-out;
  1131→}
  1132→</style>
  1133→```
