  1688→## 7. MICRO-ANIMATIONS
  1689→
  1690→### 7.1 Icon Bounce
  1691→
  1692→```css
  1693→@keyframes bounce {
  1694→  0%, 100% {
  1695→    transform: translateY(0);
  1696→  }
  1697→  50% {
  1698→    transform: translateY(-6px);
  1699→  }
  1700→}
  1701→
  1702→.icon-bounce {
  1703→  animation: bounce 1s ease-in-out;
  1704→}
  1705→
  1706→/* Interactive bounce on hover */
  1707→.icon-bounce-hover {
  1708→  transition: transform 0.2s ease-out;
  1709→}
  1710→
  1711→.icon-bounce-hover:hover {
  1712→  animation: bounce 0.6s ease-out;
  1713→}
  1714→```
  1715→
  1716→### 7.2 Number Counting Animation
  1717→
  1718→```vue
  1719→<template>
  1720→  <div class="text-3xl font-bold">
  1721→    {{ displayValue }}
  1722→    <span v-if="suffix" class="text-lg">{{ suffix }}</span>
  1723→  </div>
  1724→</template>
  1725→
  1726→<script setup lang="ts">
  1727→import { ref, onMounted, watch } from 'vue'
  1728→
  1729→interface Props {
  1730→  value: number
  1731→  duration?: number
  1732→  suffix?: string
  1733→}
  1734→
  1735→const props = withDefaults(defineProps<Props>(), {
  1736→  duration: 1000
  1737→})
  1738→
  1739→const displayValue = ref(0)
  1740→
  1741→const animateNumber = (target: number) => {
  1742→  const start = displayValue.value
  1743→  const difference = target - start
  1744→  const steps = Math.ceil(props.duration / 16) // 60fps
  1745→  let stepIndex = 0
  1746→
  1747→  const interval = setInterval(() => {
  1748→    stepIndex++
  1749→    displayValue.value = Math.round(
  1750→      start + (difference * stepIndex) / steps
  1751→    )
  1752→
  1753→    if (stepIndex >= steps) {
  1754→      displayValue.value = target
  1755→      clearInterval(interval)
  1756→    }
  1757→  }, 16)
  1758→}
  1759→
  1760→onMounted(() => {
  1761→  animateNumber(props.value)
  1762→})
  1763→
  1764→watch(() => props.value, (newValue) => {
  1765→  animateNumber(newValue)
  1766→})
  1767→</script>
  1768→```
  1769→
  1770→### 7.3 Like/Favorite Heart Animation
  1771→
  1772→```vue
  1773→<template>
  1774→  <button
  1775→    @click="toggleLike"
  1776→    :aria-label="isLiked ? 'Unlike' : 'Like'"
  1777→    class="relative transition-transform"
  1778→    :class="{ 'scale-125': animating }"
  1779→  >
  1780→    <Icon
  1781→      :icon="isLiked ? 'mdi:heart' : 'mdi:heart-outline'"
  1782→      class="w-6 h-6 transition-all"
  1783→      :class="isLiked ? 'text-error-500' : 'text-secondary'"
  1784→    />
  1785→
  1786→    <!-- Floating particles (optional) -->
  1787→    <div
  1788→      v-for="particle in particles"
  1789→      :key="particle.id"
  1790→      class="absolute pointer-events-none"
  1791→      :style="particleStyle(particle)"
  1792→    >
  1793→      <Icon icon="mdi:heart" class="w-2 h-2 text-error-500" />
  1794→    </div>
  1795→  </button>
  1796→</template>
  1797→
  1798→<script setup lang="ts">
  1799→import { ref } from 'vue'
  1800→import { Icon } from '@iconify/vue'
  1801→
  1802→const isLiked = ref(false)
  1803→const animating = ref(false)
  1804→const particles = ref<Array<{ id: string; x: number; y: number }>>([])
  1805→
  1806→const toggleLike = () => {
  1807→  if (isLiked.value) {
  1808→    isLiked.value = false
  1809→  } else {
  1810→    isLiked.value = true
  1811→    playAnimation()
  1812→  }
  1813→}
  1814→
  1815→const playAnimation = () => {
  1816→  animating.value = true
  1817→
  1818→  // Create particles
  1819→  for (let i = 0; i < 5; i++) {
  1820→    particles.value.push({
  1821→      id: Math.random().toString(36).substr(2, 9),
  1822→      x: Math.random() * 40 - 20,
  1823→      y: Math.random() * 40 - 20
  1824→    })
  1825→  }
  1826→
  1827→  setTimeout(() => {
  1828→    animating.value = false
  1829→  }, 200)
  1830→
  1831→  // Remove particles after animation
  1832→  setTimeout(() => {
  1833→    particles.value = []
  1834→  }, 600)
  1835→}
  1836→
  1837→const particleStyle = (particle: { x: number; y: number }) => ({
  1838→  left: particle.x + 'px',
  1839→  top: particle.y + 'px',
  1840→  animation: 'float-up 0.6s ease-out forwards'
  1841→})
  1842→</script>
  1843→
  1844→<style scoped>
  1845→@keyframes float-up {
  1846→  to {
  1847→    transform: translateY(-30px);
  1848→    opacity: 0;
  1849→  }
  1850→}
  1851→</style>
  1852→```
  1853→
  1854→---
  1855→
  1856→## 8. GESTURE FEEDBACK
  1857→
  1858→### 8.1 Pull-to-Refresh
  1859→
  1860→```vue
  1861→<template>
  1862→  <div
  1863→    class="relative overflow-y-auto h-screen"
  1864→    @touchmove="handleTouchMove"
  1865→    @touchend="handleTouchEnd"
  1866→  >
  1867→    <!-- Refresh indicator -->
  1868→    <div
  1869→      class="absolute top-0 left-0 right-0 flex items-center justify-center h-16 transition-all"
  1870→      :style="{ transform: `translateY(${Math.min(pullDistance, 64)}px)` }"
  1871→    >
  1872→      <div
  1873→        v-if="pullDistance > 0"
  1874→        class="w-6 h-6 rounded-full border-2 border-primary-500 border-t-transparent"
  1875→        :style="{ transform: `rotate(${pullDistance * 5}deg)` }"
  1876→      ></div>
  1877→      <span v-if="pullDistance > 64" class="ml-2 text-sm font-medium">
  1878→        Release to refresh
  1879→      </span>
  1880→    </div>
  1881→
  1882→    <!-- Content -->
  1883→    <div class="pt-4">
  1884→      <slot />
  1885→    </div>
  1886→  </div>
  1887→</template>
  1888→
  1889→<script setup lang="ts">
  1890→import { ref } from 'vue'
  1891→
  1892→const pullDistance = ref(0)
  1893→let startY = 0
  1894→let isRefreshing = false
  1895→
  1896→const emit = defineEmits<{
  1897→  refresh: []
  1898→}>()
  1899→
  1900→const handleTouchMove = (event: TouchEvent) => {
  1901→  const scrollTop = (event.currentTarget as HTMLElement).scrollTop
  1902→
  1903→  if (scrollTop === 0) {
  1904→    const currentY = event.touches[0].clientY
  1905→    if (startY === 0) startY = currentY
  1906→
  1907→    pullDistance.value = Math.max(0, currentY - startY)
  1908→  }
  1909→}
  1910→
  1911→const handleTouchEnd = () => {
  1912→  if (pullDistance.value > 64 && !isRefreshing) {
  1913→    isRefreshing = true
  1914→    emit('refresh')
  1915→
  1916→    setTimeout(() => {
  1917→      pullDistance.value = 0
  1918→      startY = 0
  1919→      isRefreshing = false
  1920→    }, 1500)
  1921→  } else {
  1922→    pullDistance.value = 0
  1923→    startY = 0
  1924→  }
  1925→}
  1926→</script>
  1927→```
  1928→
  1929→### 8.2 Drag & Drop Feedback
  1930→
  1931→```vue
  1932→<template>
  1933→  <div class="space-y-4">
