   165→  --color-brand: oklch(0.37 0.18 276); /* Overrides work */
   166→}
   167→
   168→/* Use @theme inline for indirection */
   169→@theme inline {
   170→  --color-primary: var(--semantic-primary);
   171→}
   172→
   173→:root {
   174→  --semantic-primary: oklch(0.57 0.24 276);
   175→}
   176→
   177→.theme-blue {
   178→  --semantic-primary: oklch(0.55 0.22 240);
   179→}
