   309→// Generate CSS from tokens
   310→export function generateThemeCSS() {
   311→  const entries = Object.entries(tokens.color.brand)
   312→    .map(([key, value]) => `--color-brand-${key}: ${value};`)
   313→  return entries.join('\n')
   314→}
