   607→  body {
   608→    margin: 0;
   609→    line-height: 1.5;
   610→  }
   611→  
   612→  img, picture, video, canvas, svg {
   613→    display: block;
   614→    max-width: 100%;
   615→  }
   616→  
   617→  button, input, textarea, select {
