  1206→## TypeScript Integration
  1207→
  1208→### Type-Safe Utilities
  1209→
  1210→```ts
  1211→// src/utils/cn.ts
  1212→import { type ClassValue, clsx } from 'clsx'
  1213→import { twMerge } from 'tailwind-merge'
  1214→
  1215→export function cn(...inputs: ClassValue[]) {
  1216→  return twMerge(clsx(inputs))
  1217→}
  1218→
  1219→// Usage
  1220→import { cn } from '@/utils/cn'
  1221→
  1222→const classes = cn(
  1223→  'base-class',
  1224→  isActive && 'active-class',
  1225→  { 'conditional-class': someCondition }
  1226→)
