  2005→    md: 'px-4 py-2',
  2006→    lg: 'px-6 py-3 text-lg'
  2007→  }
  2008→} as const
  2009→
  2010→export type ButtonVariant = keyof typeof buttonVariants.variant
  2011→export type ButtonSize = keyof typeof buttonVariants.size
