  2042→
  2043→/* Layout utilities */
  2044→@utility container-prose {
  2045→  max-width: 65ch;
  2046→  margin-inline: auto;
  2047→}
  2048→
  2049→/* Component utilities */
  2050→@utility card { /* ... */ }
  2051→
  2052→/* Animation utilities */
  2053→@utility slide-in { /* ... */ }
  2054→```
  2055→
  2056→### 10. Performance-First Approach
  2057→
  2058→```vue
  2059→<script setup lang="ts">
  2060→// ✅ Static classes - generated at build time
  2061→const baseClasses = 'p-4 rounded-lg shadow-md'
  2062→
  2063→// ✅ Conditional classes - still static
  2064→const variantClasses = computed(() =>
  2065→  props.variant === 'primary' 
  2066→    ? 'bg-blue-500 text-white'
  2067→    : 'bg-gray-200 text-gray-900'
  2068→)
  2069→
  2070→// ❌ Avoid runtime concatenation
  2071→const dynamicClasses = computed(() => 
  2072→  `text-${props.size}` // Can't be statically analyzed
  2073→)
  2074→
  2075→// ✅ Map to complete class names
  2076→const sizeClasses = computed(() => ({
  2077→  sm: 'text-sm',
  2078→  md: 'text-base',
  2079→  lg: 'text-lg'
  2080→}[props.size]))
