    87→}>();
    88→
    89→const sizeClass = computed(() => {
    90→  const sizeMap = {
    91→    xs: "px-2 py-1 text-xs gap-1",
    92→    sm: "px-3 py-2 text-sm gap-2",
    93→    md: "px-4 py-2 text-base gap-2",
    94→    lg: "px-6 py-3 text-lg gap-3",
    95→    xl: "px-8 py-4 text-xl gap-3",
    96→  };
    97→  return sizeMap[props.size];
    98→});
    99→
   100→const variantClass = computed(() => {
   101→  const variantMap = {
   102→    primary: "bg-primary-500 hover:bg-primary-600 active:bg-primary-700 text-white border-transparent shadow-md hover:shadow-lg focus-ring",
   103→    secondary: "bg-gray-500 hover:bg-gray-600 active:bg-gray-700 text-white border-transparent shadow-md hover:shadow-lg focus-ring",
   104→    outline: "border-2 border-primary-500 text-primary-600 hover:bg-primary-500 hover:text-white dark:text-primary-400 dark:hover:text-white bg-transparent shadow-sm hover:shadow-md focus-ring",
   105→    ghost: "text-primary-600 hover:bg-primary-50 active:bg-primary-100 dark:text-primary-400 dark:hover:bg-primary-950 dark:active:bg-primary-900 border-transparent focus-ring",
   106→    danger: "bg-error-500 hover:bg-error-600 active:bg-error-700 text-white border-transparent shadow-md hover:shadow-lg focus-ring",
   107→    success: "bg-success-500 hover:bg-success-600 active:bg-success-700 text-white border-transparent shadow-md hover:shadow-lg focus-ring",
   108→    warning: "bg-warning-500 hover:bg-warning-600 active:bg-warning-700 text-white border-transparent shadow-md hover:shadow-lg focus-ring",
   109→  };
   110→  return variantMap[props.variant];
   111→});
   112→
   113→const iconClass = computed(() => {
   114→  const iconSizeMap = {
   115→    xs: "w-3 h-3",
   116→    sm: "w-4 h-4",
   117→    md: "w-4 h-4",
   118→    lg: "w-5 h-5",
