   497→  /* ==========================================
   498→   * MODERN DEPTH & ELEVATION SYSTEM
   499→   * Research-based 2024-2025 depth principles
   500→   * ========================================== */
   501→
   502→  /* Layered Shadow System - Combines ambient + contact shadows */
   503→  .shadow-depth-0 {
   504→    box-shadow: none;
   505→  }
   506→
   507→  .shadow-depth-1 {
   508→    box-shadow:
   509→      0px 1px 2px rgba(0, 0, 0, 0.06),
   510→      0px 0px 1px rgba(0, 0, 0, 0.08);
   511→  }
   512→
   513→  .shadow-depth-2 {
   514→    box-shadow:
   515→      0px 2px 4px rgba(0, 0, 0, 0.08),
   516→      0px 0px 2px rgba(0, 0, 0, 0.06);
   517→  }
   518→
   519→  .shadow-depth-4 {
   520→    box-shadow:
   521→      0px 4px 8px rgba(0, 0, 0, 0.10),
   522→      0px 0px 2px rgba(0, 0, 0, 0.06);
   523→  }
   524→
   525→  .shadow-depth-6 {
   526→    box-shadow:
   527→      0px 6px 12px rgba(0, 0, 0, 0.12),
   528→      0px 2px 4px rgba(0, 0, 0, 0.08);
   529→  }
   530→
   531→  .shadow-depth-8 {
   532→    box-shadow:
   533→      0px 8px 16px rgba(0, 0, 0, 0.14),
   534→      0px 2px 4px rgba(0, 0, 0, 0.08);
   535→  }
   536→
   537→  .shadow-depth-12 {
   538→    box-shadow:
   539→      0px 12px 24px rgba(0, 0, 0, 0.16),
   540→      0px 4px 8px rgba(0, 0, 0, 0.10);
   541→  }
   542→
   543→  .shadow-depth-16 {
   544→    box-shadow:
   545→      0px 16px 32px rgba(0, 0, 0, 0.18),
   546→      0px 4px 8px rgba(0, 0, 0, 0.10);
   547→  }
   548→
   549→  .shadow-depth-24 {
