   551→      0px 24px 48px rgba(0, 0, 0, 0.20),
   552→      0px 8px 16px rgba(0, 0, 0, 0.12);
   553→  }
   554→
   555→  /* Color-Matched Shadows - Theme-aware depth */
   556→  .shadow-primary {
