   609→  }
   610→
   611→  [data-theme="dark"] .shadow-depth-12 {
   612→    box-shadow:
   613→      0px 12px 24px rgba(0, 0, 0, 0.55),
   614→      0px 4px 8px rgba(0, 0, 0, 0.45);
   615→  }
   616→
   617→  [data-theme="dark"] .shadow-depth-16 {
   618→    box-shadow:
   619→      0px 16px 32px rgba(0, 0, 0, 0.60),
