   668→
   669→  [data-theme="dark"] .neomorph-raised {
   670→    background: var(--color-dark-surface-secondary);
   671→    box-shadow:
   672→      12px 12px 24px rgba(0, 0, 0, 0.50),
   673→      -12px -12px 24px rgba(55, 65, 81, 0.12);
   674→  }
   675→
   676→  /* Glassmorphism 2.0 - Enhanced with saturation */
   677→  .glass-vibrant {
   678→    background: rgba(255, 255, 255, 0.70);
   679→    backdrop-filter: blur(20px) saturate(180%);
   680→    -webkit-backdrop-filter: blur(20px) saturate(180%);
   681→    border: 1px solid rgba(255, 255, 255, 0.3);
   682→    box-shadow:
