   691→      0 8px 32px rgba(0, 0, 0, 0.30),
   692→      inset 0 1px 0 rgba(255, 255, 255, 0.1);
   693→  }
   694→
   695→  .glass-frosted {
   696→    background: rgba(255, 255, 255, 0.50);
   697→    backdrop-filter: blur(32px) saturate(200%);
   698→    -webkit-backdrop-filter: blur(32px) saturate(200%);
   699→    border: 1px solid rgba(255, 255, 255, 0.25);
   700→    box-shadow:
   701→      0 12px 48px rgba(0, 0, 0, 0.10),
   702→      inset 0 1px 0 rgba(255, 255, 255, 0.5);
   703→  }
   704→
   705→  [data-theme="dark"] .glass-frosted {
   706→    background: rgba(17, 24, 39, 0.50);
   707→    border: 1px solid rgba(55, 65, 81, 0.35);
   708→    box-shadow:
   709→      0 12px 48px rgba(0, 0, 0, 0.40),
   710→      inset 0 1px 0 rgba(255, 255, 255, 0.08);
   711→  }
   712→
   713→  /* Elevation Semantic Utilities */
   714→  .elevation-surface {
   715→    box-shadow: none;
   716→    position: relative;
   717→    z-index: 0;
   718→  }
   719→
   720→  .elevation-raised {
   721→    box-shadow:
   722→      0px 2px 4px rgba(0, 0, 0, 0.08),
   723→      0px 0px 2px rgba(0, 0, 0, 0.06);
   724→    position: relative;
   725→    z-index: 1;
   726→  }
   727→
   728→  .elevation-overlay {
   729→    box-shadow:
   730→      0px 4px 8px rgba(0, 0, 0, 0.10),
   731→      0px 0px 2px rgba(0, 0, 0, 0.06);
   732→    position: relative;
   733→    z-index: 10;
   734→  }
   735→
   736→  .elevation-floating {
   737→    box-shadow:
   738→      0px 8px 16px rgba(0, 0, 0, 0.14),
   739→      0px 2px 4px rgba(0, 0, 0, 0.08);
   740→    position: relative;
   741→    z-index: 50;
   742→  }
   743→
   744→  .elevation-modal {
   745→    box-shadow:
   746→      0px 16px 32px rgba(0, 0, 0, 0.18),
   747→      0px 4px 8px rgba(0, 0, 0, 0.10);
   748→    position: relative;
   749→    z-index: var(--z-index-modal);
   750→  }
   751→
   752→  /* Depth Layering - Overlapping elements */
   753→  .layer-back {
   754→    position: relative;
   755→    z-index: 1;
   756→    transform: translateY(8px);
   757→    opacity: 0.90;
   758→    filter: blur(0.5px);
   759→  }
   760→
   761→  .layer-middle {
   762→    position: relative;
   763→    z-index: 2;
   764→    transform: translateY(4px);
   765→    opacity: 0.95;
   766→  }
   767→
   768→  .layer-front {
   769→    position: relative;
   770→    z-index: 3;
   771→    transform: translateY(0px);
   772→  }
   773→
   774→  /* Depth through scale and contrast */
   775→  .depth-recede {
   776→    opacity: 0.80;
   777→    filter: brightness(0.95) saturate(0.90);
   778→    transform: scale(0.98);
   779→  }
   780→
   781→  .depth-neutral {
   782→    opacity: 1;
   783→    filter: none;
   784→    transform: scale(1);
   785→  }
   786→
   787→  .depth-advance {
   788→    opacity: 1;
   789→    filter: brightness(1.05) saturate(1.10);
   790→    transform: scale(1.02);
   791→  }
   792→
   793→  /* Depth transitions */
   794→  .depth-transition {
   795→    transition:
   796→      transform 300ms cubic-bezier(0, 0, 0.2, 1),
   797→      box-shadow 300ms cubic-bezier(0, 0, 0.2, 1),
   798→      opacity 300ms cubic-bezier(0, 0, 0.2, 1),
   799→      filter 300ms cubic-bezier(0, 0, 0.2, 1);
   800→  }
   801→
   802→  .depth-transition:hover {
   803→    transform: translateY(-4px) scale(1.02);
   804→  }
   805→
   806→  /* Reduce depth effects for accessibility */
   807→  @media (prefers-reduced-motion: reduce) {
   808→    .depth-transition {
   809→      transition: none;
   810→    }
   811→
   812→    .depth-transition:hover {
   813→      transform: none;
   814→    }
   815→
   816→    .layer-back,
   817→    .layer-middle,
   818→    .layer-front {
   819→      transform: none;
   820→      filter: none;
   821→    }
   822→  }
   823→
   824→  /* Performance optimization for mobile */
   825→  @media (max-width: 768px) {
   826→    .glass-vibrant,
   827→    .glass-frosted {
   828→      backdrop-filter: blur(12px);
   829→      -webkit-backdrop-filter: blur(12px);
   830→    }
   831→
   832→    .neomorph-flat,
   833→    .neomorph-pressed,
   834→    .neomorph-raised {
   835→      box-shadow:
   836→        4px 4px 8px rgba(0, 0, 0, 0.10),
   837→        -4px -4px 8px rgba(255, 255, 255, 0.50);
   838→    }
   839→  }
   840→}
