   * Research-based 2024-2025 depth principles
   * ========================================== */

  /* Layered Shadow System - Combines ambient + contact shadows */
  .shadow-depth-0 {
    box-shadow: none;
  }

  .shadow-depth-1 {
    box-shadow:
      0px 1px 2px rgba(0, 0, 0, 0.06),
      0px 0px 1px rgba(0, 0, 0, 0.08);
  }

  .shadow-depth-2 {
    box-shadow:
      0px 2px 4px rgba(0, 0, 0, 0.08),
      0px 0px 2px rgba(0, 0, 0, 0.06);
  }

  .shadow-depth-4 {
    box-shadow:
      0px 4px 8px rgba(0, 0, 0, 0.10),
      0px 0px 2px rgba(0, 0, 0, 0.06);
  }

  .shadow-depth-6 {
    box-shadow:
      0px 6px 12px rgba(0, 0, 0, 0.12),
      0px 2px 4px rgba(0, 0, 0, 0.08);
  }

  .shadow-depth-8 {
    box-shadow:
      0px 8px 16px rgba(0, 0, 0, 0.14),
      0px 2px 4px rgba(0, 0, 0, 0.08);
  }

  .shadow-depth-12 {
    box-shadow:
      0px 12px 24px rgba(0, 0, 0, 0.16),
      0px 4px 8px rgba(0, 0, 0, 0.10);
  }

  .shadow-depth-16 {
    box-shadow:
      0px 16px 32px rgba(0, 0, 0, 0.18),
      0px 4px 8px rgba(0, 0, 0, 0.10);
  }

  .shadow-depth-24 {
    box-shadow:
      0px 24px 48px rgba(0, 0, 0, 0.20),
      0px 8px 16px rgba(0, 0, 0, 0.12);
  }

  /* Color-Matched Shadows - Theme-aware depth */
  .shadow-primary {
    box-shadow:
      0px 4px 12px oklch(50% 0.16 200 / 0.15),
      0px 0px 4px oklch(50% 0.16 200 / 0.10);
  }

  .shadow-success {
    box-shadow:
      0px 4px 12px oklch(65% 0.17 160 / 0.15),
      0px 0px 4px oklch(65% 0.17 160 / 0.10);
  }

  .shadow-warning {
    box-shadow:
      0px 4px 12px oklch(75% 0.18 85 / 0.15),
      0px 0px 4px oklch(75% 0.18 85 / 0.10);
  }

  .shadow-error {
    box-shadow:
      0px 4px 12px oklch(62% 0.22 25 / 0.15),
      0px 0px 4px oklch(62% 0.22 25 / 0.10);
