   477→  }
   478→  to {
   479→    opacity: 1;
   480→    transform: scale(1);
   481→  }
   482→}
   483→
   484→@keyframes scaleOut {
   485→  from {
   486→    opacity: 1;
   487→    transform: scale(1);
   488→  }
   489→  to {
   490→    opacity: 0;
   491→    transform: scale(0.9);
   492→  }
   493→}
   494→
   495→@keyframes pulse {
   496→  0%, 100% {
   497→    opacity: 1;
   498→  }
   499→  50% {
   500→    opacity: 0.5;
   501→  }
   502→}
   503→
   504→@keyframes bounce {
